INSERT INTO `srtphire` (`id`, `oid`, `title`, `orienting`, `content`, `author`, `original_time`, `breviary`) VALUES
(1, 'CB26033D8EB77E0F', '新型数据中心网络架构设计和研究', '全校\r', '\n		    <p>\n	<span style="font-size:12.0pt;line-height:150%;font-family:宋体;">数据中心网络通过高速链路和交换机支持数万至数十万台服务器间的互联，提供高效的网络通信和数据传输保障，其互联方式和组网架构对于数据中心的传输性能、能效和成本具有非常重要的作用。<span></span></span>\n</p>\n<p>\n	主要完成任务： <span></span>\n</p>\n<p>\n	<span>1</span>、了解学习当前数据中心网络架构； <span></span>\n</p>\n<p>\n	<span>2</span>、设计提出新型的数据中心网络，并进行仿真研究； <span></span>\n</p>\n<p>\n	主要要求： <span></span>\n</p>\n<p>\n	<span>1</span>、对此项目有一定的兴趣，在不耽误学习的情况有时间完成此项目。 <span></span>\n</p>\n<p>\n	<span>2</span>、有一定的英文文献阅读能力和编程能力，最好能持续做下去<span></span>\n</p>\n<p>\n	<span>&nbsp;</span>\n</p>\n<p>\n	有意向同学将具体情况发到我邮箱<span><a href="mailto:yanghuifly@home.swjtu.edu.cn">yanghuifly@home.swjtu.edu.cn</a> </span>\n</p>\n		  ', '杨慧', '2016-03-15 23:26:37', ''),
(2, '16257854D3B63295', 'HDPE膜缺陷渗漏试验及计算模型研究、裂隙非达西流水力参数沿程变化规律研究、农村公路交通噪声距离衰减系数研究', '全校\r', '\r\n		    <span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span lang="EN-US" style="color:#3333FF;font-family:&quot;微软雅黑&quot;,sans-serif;font-size:12pt;mso-bidi-font-size:11.0pt;">1</span></b><b style="mso-bidi-font-weight:normal;"><span style="color:#3333FF;font-family:&quot;微软雅黑&quot;,sans-serif;font-size:12pt;mso-bidi-font-size:11.0pt;">、<span lang="EN-US">HDPE</span>膜缺陷渗漏试验及计算模型研究</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span style="font-family:宋体;font-size:small;">主要内容：</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span lang="EN-US">1</span>）购买不同规格（厚度）的<span lang="EN-US">HDPE</span>膜，剪裁加工并施做钻孔或裂缝（用于模拟缺陷）；</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span lang="EN-US">2</span>）构建试验模型（高约<span lang="EN-US">1.2m</span>，直径约<span lang="EN-US">20cm</span>，有机玻璃材质；分为上下两端，上段约<span lang="EN-US">40cm</span>，下段约<span lang="EN-US">80cm</span>，中间用法兰盘连接）；</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span lang="EN-US">3</span>）试验不同<span lang="EN-US">HDPE</span>膜厚度、不同缺陷尺寸、不同缺陷形状和不同水头压力下，缺陷是渗漏量；</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span lang="EN-US">4</span>）研究缺陷渗漏量的经验计算模型（有条件可以推到解析解和用软件模拟）。</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span lang="EN-US"><span style="font-family:宋体;font-size:small;">&nbsp;</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span style="font-family:宋体;font-size:small;">要求：</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:small;">有较强的动手能力和一定的数学和计算机基础。</span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span lang="EN-US"><span style="font-family:宋体;font-size:small;">&nbsp;</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span style="font-family:宋体;font-size:small;">可供参考资料：</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span style="mso-bidi-font-family:宋体;">①</span>复合土工膜缺陷渗漏试验的饱和<span lang="EN-US">_</span>非饱和渗流有限元模拟</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span style="mso-bidi-font-family:宋体;">②</span>复合土工膜选型及缺陷渗漏量试验研究</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:small;">……等</span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span lang="EN-US"><span style="font-family:宋体;font-size:small;">&nbsp;</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span lang="EN-US" style="color:#3333FF;font-family:&quot;微软雅黑&quot;,sans-serif;font-size:12pt;mso-bidi-font-size:11.0pt;">2</span></b><b style="mso-bidi-font-weight:normal;"><span style="color:#3333FF;font-family:&quot;微软雅黑&quot;,sans-serif;font-size:12pt;mso-bidi-font-size:11.0pt;">、裂隙非达西流水力参数沿程变化规律研究</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span style="font-family:宋体;font-size:small;">主要内容：</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span lang="EN-US">1</span>）构建裂隙渗流模型（在已有裂隙模型的基础上，进行改进创新）；</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span lang="EN-US">2</span>）试验和测量整条裂隙及裂隙的不同分段在各压力条件下的水头损失、流速；</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span lang="EN-US">3</span>）采用<span lang="EN-US">Forchheimer</span>模型计算各裂隙段和整条裂隙的非达西流水力参数；</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span lang="EN-US">4</span>）研究裂隙非达西流水力参数的沿程变化规律。</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span lang="EN-US"><span style="font-family:宋体;font-size:small;">&nbsp;</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span style="font-family:宋体;font-size:small;">要求：</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:small;">有较强的动手能力和一定的数学和计算机基础。</span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span lang="EN-US"><span style="font-family:宋体;font-size:small;">&nbsp;</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span style="font-family:宋体;font-size:small;">可供参考资料：</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span style="mso-bidi-font-family:宋体;">①</span>单裂隙承压无压条件下达西<span lang="EN-US">_</span>非达西流演变试验研究</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span style="mso-bidi-font-family:宋体;">②</span>光滑裂隙高流速非达西渗流运动规律的试验研究</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:small;">……等</span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span lang="EN-US"><span style="font-family:宋体;font-size:small;">&nbsp;</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span lang="EN-US"><span style="font-family:宋体;font-size:small;">&nbsp;</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span lang="EN-US" style="color:#3333FF;font-family:&quot;微软雅黑&quot;,sans-serif;font-size:12pt;mso-bidi-font-size:11.0pt;">3</span></b><b style="mso-bidi-font-weight:normal;"><span style="color:#3333FF;font-family:&quot;微软雅黑&quot;,sans-serif;font-size:12pt;mso-bidi-font-size:11.0pt;">、农村公路交通噪声距离衰减系数研究</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span style="font-family:宋体;font-size:small;">主要内容：</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span lang="EN-US">1</span>）选取学校周边具有代表意义的农村公路进行调查；</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span lang="EN-US">2</span>）调查内容主要包括车流量、车型比、距路中心线不同距离处的交通噪声值；</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span lang="EN-US">3</span>）研究其随距离的变化规律，并采用《环境影响评价技术导则 声环境》（<span lang="EN-US">HJ2.4-2009</span>）中推荐的模式进行计算和比较，提出模式修正建议或适用条件的建议。</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span lang="EN-US"><span style="font-family:宋体;font-size:small;">&nbsp;</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span style="font-family:宋体;font-size:small;">要求：</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:small;">有较强的实践动手能力。</span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span lang="EN-US"><span style="font-family:宋体;font-size:small;">&nbsp;</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span style="font-family:宋体;font-size:small;">可供参考资料：</span></b>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span style="mso-bidi-font-family:宋体;">①</span>以摩托车为主的道路交通噪声预测模型</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><span style="font-family:宋体;"><span style="mso-bidi-font-family:宋体;">②</span>环境影响评价技术导则声环境（<span lang="EN-US">HJ2.4-2009</span>）</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:small;">……等</span>\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:small;"><br />\r\n</span>\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:small;"><span style="font-family:宋体;font-size:10.5pt;mso-bidi-font-size:11.0pt;mso-bidi-font-family:&quot;Times New Roman&quot;;mso-font-kerning:1.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;"><strong>总体要求：<span style="color:red;">具有较强的动手能力，同时时间相对充裕。相同条件下，踏实、勤奋者优先考虑。</span></strong></span></span>\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:small;"><span style="font-family:宋体;font-size:10.5pt;mso-bidi-font-size:11.0pt;mso-bidi-font-family:&quot;Times New Roman&quot;;mso-font-kerning:1.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;"><strong><span style="color:red;"><br />\r\n</span></strong></span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;">\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<br />\r\n</p>\r\n<span style="font-family:宋体;font-size:10.5pt;mso-bidi-font-size:11.0pt;mso-bidi-font-family:&quot;Times New Roman&quot;;mso-font-kerning:1.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;"><span style="color:red;">\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<b style="mso-bidi-font-weight:normal;"><span style="color:#3333FF;">导师基本信息：</span></b>\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="color:#000000;font-size:small;"> </span><span style="color:#000000;">刘建：地球科学与环境工程学院<span lang="EN-US"><span style="mso-spacerun:yes;">&nbsp; </span></span>副研究员</span>\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="color:#000000;font-size:small;"> </span><span style="color:#000000;">主要授课：环境保护与可持续发展，固体废物与可持续发展（新生研讨课）</span>\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="color:#000000;font-size:small;"> </span><span style="color:#000000;">主要研究方向：隧道工程地下水环境影响及其控制、固体废物处理与处置、交通噪声预测</span>\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="color:#000000;"><span style="font-size:small;"> </span><span style="font-family:宋体;font-size:10.5pt;mso-bidi-font-size:11.0pt;mso-bidi-font-family:&quot;Times New Roman&quot;;mso-font-kerning:1.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;">联系方式：<span lang="EN-US">QQ</span>：<span lang="EN-US">546606342</span>；电话：<span lang="EN-US">13518162186。请组好3-5人研究队伍后联系。</span></span></span>\r\n</p>\r\n</span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<br />\r\n</p>\r\n</span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<br />\r\n</p>\r\n</span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<br />\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n		  ', '刘建', '2016-03-15 23:21:23', NULL),
(3, '32B2766F0B57FDB6', '接入网络中时频资源分配算法研究', '全校\r', '\r\n		    <p>\r\n	光纤入户是国家大力发展的宽带接入方案，是通信传输系统的未来中坚力量。在光纤入户的各种方案中，如何合理公平的分配资源是非常重要的一个话题。 <span></span>\r\n</p>\r\n<p>\r\n	主要完成任务： <span></span>\r\n</p>\r\n<p>\r\n	<span>1</span>、了解学习当前接入网络中的带宽分配算法； <span></span>\r\n</p>\r\n<p>\r\n	<span>2</span>、提出新型的时频资源分配方案并编程实现（可能基于<span>MATLAB</span>）； <span></span>\r\n</p>\r\n<p>\r\n	主要要求： <span></span>\r\n</p>\r\n<p>\r\n	<span>1</span>、对此项目有一定的兴趣，在不耽误学习的情况有时间完成此项目。 <span></span>\r\n</p>\r\n<p>\r\n	<span>2</span>、有一定的英文文献阅读能力和编程能力，最好能持续做下去<span></span>\r\n</p>\r\n<p>\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p>\r\n	<span>&nbsp; </span>\r\n</p>\r\n<p>\r\n	有意向同学将具体情况发到我邮箱<span><a href="mailto:yanghuifly@home.swjtu.edu.cn">yanghuifly@home.swjtu.edu.cn</a> </span>\r\n</p>\r\n		  ', '杨慧', '2016-03-15 22:40:18', NULL),
(4, '269778D9FBBACF7B', '云计算环境下的语音真实性鉴别系统设计及实现', '信息科学与技术学院\r', '\r\n		    <p style="text-indent:24pt;">\r\n	<span style="line-height:150%;font-family:宋体;font-size:12pt;">近年来，数字<span style="color:black;">化语音通信</span></span><span style="line-height:150%;font-family:宋体;font-size:12pt;">的迅猛发展</span><span style="color:black;line-height:150%;font-family:宋体;font-size:12pt;">和各种语音产品的广泛普及，以及</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">各种功能强大的音频处理软件</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">的出现，使得数字语音的传输与应用日益变得频繁与广泛。但是，目前互联网的存储量与计算体系结构已经不能满足超大规模多媒体数据管理与计算的要求。云计算的出现为多媒体服务提供了新思路。在云环境下，用户的数据不是存储于本地计算机上，而是存储在远程云服务器中，这增加了数据保密性的隐忧。在面向云计算的语音安全应用中</span><span style="line-height:150%;font-size:12pt;">,</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">如大规模电信语音、法律证据和金融操作录音及语音订单业务中涉及用户隐私的敏感语音等</span><span style="line-height:150%;font-size:12pt;">,</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">需加密后存储在云端，达到隐私保护的目的。与此同时，对于存储在云端的密文语音很难确保其在云存储、网络传送过程中要害部分内容不被恶意篡改，甚至在返回用户语音数据时会出现“张冠李戴”、“偷梁换柱”等出错情况。因此，用户如何鉴别从云端获取的重要密文语音是否为原始语音，密文语音是否被篡改过，哪里被篡改了</span><span style="color:black;line-height:150%;font-family:宋体;font-size:12pt;">，这种涉及</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">密文语音真实性鉴别问题</span><span style="color:black;line-height:150%;font-family:宋体;font-size:12pt;">，对</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">云计算环境下的数字语音安全提出了一个新问题。</span><span style="line-height:150%;font-size:12pt;"></span>\r\n</p>\r\n<p style="text-indent:24pt;">\r\n	<span style="line-height:150%;font-family:宋体;font-size:12pt;">本项目主要内容包括：（</span><span style="line-height:150%;font-size:12pt;">1</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">）对敏感语音加密，达到隐私保护的目的；（</span><span style="line-height:150%;font-size:12pt;">2</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">）密文语音真实性鉴别技术研究。研究成果将为云环境下隐私性语音数据的安全保护提供理论与技术支持。</span><span style="line-height:150%;font-family:宋体;font-size:12pt;"></span>\r\n</p>\r\n<br />\r\n<br />\r\n		  ', '王宏霞', '2016-03-15 22:39:11', NULL),
(5, '9D85B884EB62CC6B', '基于图像识别的道砟颗粒三维廓形重构方法与试验研究', '全校\r', '\r\n		    <p>\r\n	<span style="font-family:''Simsun'',serif;color:black;font-size:10pt;"><span style="font-family:宋体;font-size:10.5pt;"><span style="font-size:14px;">有砟轨道一直以来因为其良好的弹性与易维护性广受铁路工作人员的青睐，其中最有特点的即散体道床。散体道床由道砟颗粒组合堆积而成，其散粒体力学性能一直以来是铁路工程</span><span style="font-size:14px;">研究</span><span style="font-size:14px;">的重点，而散体道床力学的研究</span><span style="font-size:14px;">需要从实际的道砟颗粒存在现状的研究</span><span style="font-size:14px;">开始</span><span style="font-size:14px;">。为了充分了解道砟颗粒的存在状态，</span><span style="font-size:14px;">需要借助试验手段，检测道砟颗粒的三维廓形</span><span style="font-size:14px;">。本项目拟采用图像识别技术，通过拍摄道砟颗粒不同角度的二维图像，提取图形信息，通过图像算法与程序设计</span><span style="font-size:14px;">重构道砟颗粒的三维廓形，为散体道床的物理与力学性能研究奠定基础。</span></span></span>\r\n</p>\r\n<p>\r\n	<span style="font-family:''Simsun'',serif;color:black;font-size:10pt;"><span style="font-family:宋体;font-size:10.5pt;"></span></span><span style="font-family:''Simsun'',serif;color:black;font-size:10pt;"></span>&nbsp;\r\n</p>\r\n<p>\r\n	<span style="font-family:''Simsun'',serif;color:black;font-size:14px;">1.</span><span style="font-family:宋体;color:black;font-size:14px;">掌握一定铁道工程专业知识，熟悉有砟轨道结构；</span><span style="font-family:''Simsun'',serif;color:black;font-size:10pt;"><br />\r\n<span style="font-size:14px;">2.</span></span><span style="font-family:宋体;color:black;font-size:10pt;"><span style="font-size:14px;">具备较强</span><span style="font-size:14px;">的数学</span><span style="font-size:14px;">功底，有一定图像处理与数据处理的基本能力</span><span style="font-size:14px;">；</span></span><span style="font-family:''Simsun'',serif;color:black;font-size:10pt;"><br />\r\n<span style="font-size:14px;">3.</span></span><span style="font-family:宋体;color:black;font-size:14px;">掌握一定的室内外试验技术基本知识，具有较强动手能力；</span><span style="font-family:''Simsun'',serif;color:black;font-size:10pt;"><br />\r\n<span style="font-size:14px;">4.</span></span><span style="font-family:宋体;color:black;font-size:10pt;"><span style="font-size:14px;">具备文献检索能力，能够检索国内外最新</span><span style="font-size:14px;">学术论文与研究成果</span><span style="font-size:14px;">；</span></span><span style="font-family:''Simsun'',serif;color:black;font-size:10pt;"><br />\r\n<span style="font-size:14px;">5.</span><span style="font-size:14px;">能熟练运用MATLAB或其他编程软件。</span></span>\r\n</p>\r\n<p>\r\n	<span style="font-family:''Simsun'',serif;color:black;font-size:10pt;"></span>&nbsp;\r\n</p>\r\n<p>\r\n	<span style="font-family:''Simsun'',serif;color:black;font-size:10pt;"></span><span style="font-family:''Simsun'',serif;color:black;font-size:10pt;"><span style="color:black;font-size:14px;">联系方式：</span><span style="font-family:''Simsun'',serif;color:black;font-size:14px;">&nbsp;</span>\r\n</p>\r\n<p style="text-indent:19.5pt;background:white;">\r\n	<span style="font-family:''Simsun'',serif;color:black;font-size:10pt;"><span style="font-size:14px;">E-mail:</span><span style="font-size:14px;">&nbsp;</span><a href="mailto:chenrong@home.swjtu.edu.cn"><span style="font-size:14px;">chenrong@home.swjtu.edu.cn</span></a><span style="font-size:14px;">; QQ: 358346020</span></span>\r\n</p>\r\n<p style="text-indent:19.5pt;background:white;">\r\n	<span style="font-family:''Simsun'',serif;color:black;font-size:14px;">Tel: 13699008192</span>\r\n</p>\r\n</span>\r\n		  ', '陈嵘', '2016-03-15 20:32:24', NULL),
(6, '6725137F2747CA07', '大学生网络安全意识调查及影响因素探索', '全校\r', '\r\n		    <p style="-webkit-margin-before:1em;-webkit-margin-after:1em;font-family:Simsun;font-size:13px;line-height:19.5px;text-indent:26px;white-space:normal;background-color:#FFFFFF;">\r\n	具备一定的英文文献查阅与阅读能力；\r\n</p>\r\n<p style="-webkit-margin-before:1em;-webkit-margin-after:1em;font-family:Simsun;font-size:13px;line-height:19.5px;text-indent:26px;white-space:normal;background-color:#FFFFFF;">\r\n	具备一定的问卷编制和数据分析的能力(或有意愿学习)\r\n</p>\r\n<p style="-webkit-margin-before:1em;-webkit-margin-after:1em;font-family:Simsun;font-size:13px;line-height:19.5px;text-indent:26px;white-space:normal;background-color:#FFFFFF;">\r\n	信息，传播，心理等专业优先；\r\n</p>\r\n<p style="-webkit-margin-before:1em;-webkit-margin-after:1em;font-family:Simsun;font-size:13px;line-height:19.5px;text-indent:26px;white-space:normal;background-color:#FFFFFF;">\r\n	欢迎具有动画制作方面特长的同学\r\n</p>\r\n<p style="-webkit-margin-before:1em;-webkit-margin-after:1em;font-family:Simsun;font-size:13px;line-height:19.5px;text-indent:26px;white-space:normal;background-color:#FFFFFF;">\r\n	网络购物，网络支付使用较多者优先。\r\n</p>\r\n<p style="-webkit-margin-before:1em;-webkit-margin-after:1em;font-family:Simsun;font-size:13px;line-height:19.5px;text-indent:26px;white-space:normal;background-color:#FFFFFF;">\r\n	联系方式：wxq@swjtu.edu.cn\r\n</p>\r\n		  ', '王兴起', '2016-03-15 19:57:29', NULL),
(7, '77EEF342E65516DD', '大学生网络行为调查及安全应对', '全校\r', '\r\n		    <p>\r\n	具备一定的英文文献查阅与阅读能力；\r\n</p>\r\n<p>\r\n	具备一定的问卷编制和数据分析的能力(或有意愿学习)\r\n</p>\r\n<p>\r\n	信息，传播，心理等专业优先；\r\n</p>\r\n<p>\r\n	欢迎具有动画制作方面特长的同学\r\n</p>\r\n<p>\r\n	网络购物，网络支付使用较多者优先。\r\n</p>\r\n<p>\r\n	联系方式：wxq@swjtu.edu.cn\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n		  ', '王兴起', '2016-03-15 19:41:16', NULL),
(8, '06FD935C687A3592', '稀土钡铜氧高温超导薄膜的化学溶液法制备及载流特性研究', '全校\r', '\r\n		    <span style="font-family:宋体;font-size:small;"> \r\n<p>\r\n	<span style="line-height:150%;font-family:宋体;font-size:12pt;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;">项目背景：</span> \r\n</p>\r\n<p style="margin:0cm 0cm 0pt;line-height:150%;">\r\n	<span lang="EN-US" style="line-height:150%;font-size:12pt;"><span style="mso-spacerun:yes;"><span style="font-family:Calibri;"> </span></span></span><span style="line-height:150%;font-family:宋体;font-size:12pt;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;">稀土钡铜氧高温超导涂层导体即第二代高温超导带材是国际高温超导领域研发的重中之重，在液氮温区具有较高的临界电流密度和不可逆场，在电力、交通、医学、军事等领域有广泛的应用前景。超导层作为二代带材传输超导电流的主要通道，具有重要的研究意义。目前，制备超导层的方法如脉冲激光沉积、磁控溅射等需要在真空条件下，成本高昂，不利于二代带材的产业化发展。因此，采用新型低成本的化学溶液法沉积超导膜可显著降低带材应用的成本，是二代带材走向产业化的最佳解决方案之一。</span> \r\n</p>\r\n<p style="margin:0cm 0cm 0pt;line-height:150%;">\r\n	<span style="line-height:150%;font-family:宋体;font-size:12pt;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;"><br />\r\n</span> \r\n</p>\r\n<p style="margin:0cm 0cm 0pt;line-height:150%;">\r\n	<span style="line-height:150%;font-family:宋体;font-size:12pt;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;">任务要求：</span> \r\n</p>\r\n</span> \r\n<p>\r\n	<span style="font-size:small;"><span lang="EN-US" style="font-family:;" roman";mso-bidi-theme-font:minor-bidi;"="" new="" calibri","sans-serif";mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-family:"times="">1</span><span style="mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-theme-font: minor-latin;" new="" roman";mso-bidi-theme-font:minor-bidi;mso-ascii-font-family:calibri;mso-hansi-font-family:calibri;"="" times=""><span style="font-family:宋体;">）本项目要求每周</span></span><span lang="EN-US" style="font-family:;" roman";mso-bidi-theme-font:minor-bidi;"="" new="" calibri","sans-serif";mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-family:"times="">1-2</span><span style="mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-theme-font: minor-latin;" new="" roman";mso-bidi-theme-font:minor-bidi;mso-ascii-font-family:calibri;mso-hansi-font-family:calibri;"="" times=""><span style="font-family:宋体;">次实验，掌握相关实验技术；</span></span><span lang="EN-US" style="font-family:;" roman";mso-bidi-theme-font:minor-bidi;"="" new="" calibri","sans-serif";mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-family:"times="">2</span><span style="mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-theme-font: minor-latin;" new="" roman";mso-bidi-theme-font:minor-bidi;mso-ascii-font-family:calibri;mso-hansi-font-family:calibri;"="" times=""><span style="font-family:宋体;">）熟练样品的物相分析、微结构观察和载流特性表征等；</span></span><span lang="EN-US" style="font-family:;" roman";mso-bidi-theme-font:minor-bidi;"="" new="" calibri","sans-serif";mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-family:"times="">3</span><span style="mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-theme-font: minor-latin;" new="" roman";mso-bidi-theme-font:minor-bidi;mso-ascii-font-family:calibri;mso-hansi-font-family:calibri;"="" times=""><span style="font-family:宋体;">）研究热动力学参数对稀土钡铜氧超导膜的单晶取向、表面形貌、超导电性和磁性能的影响，并分析可能的原因；</span></span><span lang="EN-US" style="font-family:;" roman";mso-bidi-theme-font:minor-bidi;"="" new="" calibri","sans-serif";mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-family:"times="">4</span><span style="font-family:宋体;"><span style="mso-ascii-theme-font: minor-latin; mso-fareast-font-family: 宋体; mso-fareast-theme-font: minor-fareast; mso-hansi-theme-font: minor-latin;" new="" roman";mso-bidi-theme-font:minor-bidi;mso-ascii-font-family:calibri;mso-hansi-font-family:calibri;"="" times="">）探索稀土钡铜氧超导膜的快速制备技术。</span></span><span style="font-family:;" roman";mso-bidi-theme-font:minor-bidi;"="" new="" calibri","sans-serif";mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-family:"times="">&nbsp;</span></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:small;"><span style="font-family:;" roman";mso-bidi-theme-font:minor-bidi;"="" new="" calibri","sans-serif";mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-latin;mso-bidi-font-family:"times="">人数：4人，可在后续的毕业设计中延续该研究方向</span></span>\r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:small;"> <br />\r\n</span> \r\n</p>\r\n		  ', '王文涛', '2016-03-15 18:41:54', NULL),
(9, 'DC249D23E5DD9D68', '成灌快速铁路运营速度与环境噪声的相关性分析', '全校\r', '\r\n		    &nbsp;\r\n<p>\r\n	<span style="font-family:宋体;line-height:150%;">&nbsp;&nbsp;&nbsp; 噪声污染是铁路运营期主要的环境问题之一。随着我国快速铁路网的大规模建设运营，铁路噪声涉及的区域和影响的人群逐步扩大，引起了沿线居民极大关注和环境保护管理部门的高度重视，防治铁路沿线噪声已成为我国铁路发展规划的重点任务之一。本次通过现场监测</span><span style="font-family:宋体;">分析铁路运营速度与铁路噪声的关系</span><span style="font-family:宋体;line-height:150%;">，为铁路噪声防治提供参考。</span><span style="line-height:150%;"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="font-family:宋体;line-height:150%;">任务：（</span><span style="line-height:150%;">1</span><span style="font-family:宋体;line-height:150%;">）查阅资料，初步分析铁路噪声与运营速度的关系；（</span><span style="line-height:150%;">2</span><span style="font-family:宋体;line-height:150%;">）根据成灌快速铁路运行规律，布设监测点，用声级计监测铁路运行噪声；（</span><span style="line-height:150%;">3</span><span style="font-family:宋体;line-height:150%;">）总结监测结果，分析</span><span style="font-family:宋体;">铁路运营速度与环境噪声的相关性</span><span style="line-height:150%;"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="font-family:宋体;line-height:150%;">要求：有</span><span style="line-height:150%;">1-2</span><span style="font-family:宋体;line-height:150%;">名环境工程专业大二或大三学生参加，建议与土木、机械、交运等专业本科生组成</span><span style="line-height:150%;">3-5</span><span style="font-family:宋体;line-height:150%;">人团队。</span><span style="line-height:150%;"> <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style="font-family:宋体;line-height:150%;">联系</span><span style="line-height:150%;">444830536@qq.com</span><span style="font-family:宋体;line-height:150%;">，手机：</span><span style="line-height:150%;">13880215200</span><span style="font-family:宋体;line-height:150%;">。</span><span style="line-height:150%;"></span>\r\n</p>\r\n		  ', '王绍笳', '2016-03-15 18:23:32', NULL),
(10, '829A79C9097C5C66', '成灌快速铁路沿线噪声影响调查分析', '全校\r', '\r\n		    &nbsp;\r\n<p>\r\n	<span style="font-family:宋体;line-height:150%;">&nbsp;&nbsp;&nbsp; 噪声污染是铁路运营期主要的环境问题之一。随着我国快速铁路网的大规模建设运营，铁路噪声涉及的区域和影响的人群逐步扩大，引起了沿线居民极大关注和环境保护管理部门的高度重视，防治铁路沿线噪声已成为我国铁路发展规划的重点任务之一。本次通过现场监测</span><span style="font-family:宋体;">和问卷调查的形式，分析成灌快速铁路沿线铁路噪声对周围环境的影响</span><span style="font-family:宋体;line-height:150%;">，为铁路噪声防治提供参考。</span><span style="line-height:150%;"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="font-family:宋体;line-height:150%;">任务：（</span><span style="line-height:150%;">1</span><span style="font-family:宋体;line-height:150%;">）查阅资料和现场踏勘，确定成灌快铁沿线主要受铁路噪声影响的环境敏感目标（学校、医院、居民区等）的概况（与铁路的相互位置关系、规模）；（</span><span style="line-height:150%;">2</span><span style="font-family:宋体;line-height:150%;">）用声级计监测敏感目标的铁路噪声，分析确定铁路噪声的达标距离；（</span><span style="line-height:150%;">3</span><span style="font-family:宋体;line-height:150%;">）设计调查问卷，调查成灌快速铁路沿线铁路噪声的影响，分析其原因。</span><span style="line-height:150%;"> <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="font-family:宋体;line-height:150%;">要求：有</span><span style="line-height:150%;">1-2</span><span style="font-family:宋体;line-height:150%;">名环境工程专业大二或大三学生参加，建议与土木、机械、交运等专业本科生组成</span><span style="line-height:150%;">3-5</span><span style="font-family:宋体;line-height:150%;">人团队。</span><span style="line-height:150%;"> <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style="font-family:宋体;line-height:150%;">联系</span><span style="line-height:150%;">444830536@qq.com</span><span style="font-family:宋体;line-height:150%;">，手机：</span><span style="line-height:150%;">13880215200</span><span style="font-family:宋体;line-height:150%;">。</span><span style="line-height:150%;"></span>\r\n</p>\r\n		  ', '王绍笳', '2016-03-15 18:22:31', NULL),
(11, 'A2DF433427DF7F42', 'Mg-Y合金平面应变压缩变形研究', '材料科学与工程学院\r', '\r\n		    <div style="line-height:14.4px;white-space:normal;text-align:center;">\r\n	<strong style="white-space:nowrap;line-height:1.5;">西南交大 交通运输装备轻量化研究所欢迎你！</strong>\r\n</div>\r\n<span style="line-height:14.4px;">\r\n<div style="text-align:center;">\r\n	<span style="line-height:1.5;">http://litmat.swjtu.edu.cn/</span>\r\n</div>\r\n</span><br style="line-height:14.4px;white-space:normal;" />\r\n<span style="line-height:14.4px;">镁合金，最轻的高强度金属！借镁合金来创造--你可以的！</span><br style="line-height:14.4px;white-space:normal;" />\r\n<br style="line-height:14.4px;white-space:normal;" />\r\n<span style="line-height:14.4px;">镁合金，密度1.75g/cm3 (铝合金2.76，钛4.5，钢7.8，铜8.9，铅11.3，金19.3)，是最轻的金属材料。而且镁合金永远不会资源枯竭！（铅，锌等已经枯竭；铜，铁，铝迅速枯竭！）</span><br style="line-height:14.4px;white-space:normal;" />\r\n<br style="line-height:14.4px;white-space:normal;" />\r\n<span style="line-height:14.4px;">交通运输装备（车辆）要轻（轻量化）：运载火箭减重1磅，节约发射资金40000美元！高速列车减重8%，综合节电17%！</span><br style="line-height:14.4px;white-space:normal;" />\r\n<br style="line-height:14.4px;white-space:normal;" />\r\n<span style="line-height:14.4px;">先进镁合金 + 先进成型技术与装备 = 新工业时代的宠儿！</span><br style="line-height:14.4px;white-space:normal;" />\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<strong>项目简介：</strong>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	（1）相对于单轴应力应变实验来说，平面应变压缩实验具有应变量大、变形均匀等优点，尤其适合研究一些塑性不好的金属（如密排六方结构的镁合金）的变形行为及其在变形过程中的组织变化规律。而Mg-Y基合金作为具有巨大潜力的高性能镁合金，目前为止，关于其平面应变压缩变形的研究极少；\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n（2）由于平面应变压缩的变形条件和边界条件与板带轧制的基本一致，所以，研究Mg-Y合金在平面应变压缩条件下的变形行为，对模拟以及指导Mg-Y基合金板材的轧制工艺优化，具有重要的指导意义。\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<span style="line-height:1.5;white-space:nowrap;"><br />\r\n</span>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<span style="line-height:1.5;white-space:nowrap;">研究内容包括但不限于：Y元素对平面压缩行为影响；不同应变量的组织演化规律；与单轴拉伸或者压缩变形的对比；</span><span style="line-height:1.5;"></span>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<strong style="line-height:1.5;"><br />\r\n</strong>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<strong style="line-height:1.5;">联系方式：</strong><span style="line-height:1.5;">ahnydd@swjtu.edu.cn</span><span style="line-height:1.5;"></span>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<strong><br />\r\n</strong>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<strong>导师简介：</strong>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<span style="white-space:nowrap;">http://userweb.swjtu.edu.cn/Userweb/ahnydd/index.htm</span>\r\n</p>\r\n		  ', '尹冬弟', '2016-03-15 17:36:40', NULL),
(12, '78C3841DBD273001', 'Y元素对镁合金可锻性的影响', '材料科学与工程学院\r', '\r\n		    <div style="line-height:14.4px;white-space:normal;text-align:center;">\r\n	<strong style="white-space:nowrap;line-height:1.5;">西南交大 交通运输装备轻量化研究所欢迎你！</strong>\r\n</div>\r\n<div style="line-height:14.4px;white-space:normal;text-align:center;">\r\n	<span style="line-height:1.5;">http://litmat.swjtu.edu.cn/</span>\r\n</div>\r\n<br style="line-height:14.4px;white-space:normal;" />\r\n<span style="line-height:14.4px;white-space:normal;">镁合金，最轻的高强度金属！借镁合金来创造--你可以的！</span><br style="line-height:14.4px;white-space:normal;" />\r\n<br style="line-height:14.4px;white-space:normal;" />\r\n<span style="line-height:14.4px;white-space:normal;">镁合金，密度1.75g/cm3 (铝合金2.76，钛4.5，钢7.8，铜8.9，铅11.3，金19.3)，是最轻的金属材料。而且镁合金永远不会资源枯竭！（铅，锌等已经枯竭；铜，铁，铝迅速枯竭！）</span><br style="line-height:14.4px;white-space:normal;" />\r\n<br style="line-height:14.4px;white-space:normal;" />\r\n<span style="line-height:14.4px;white-space:normal;">交通运输装备（车辆）要轻（轻量化）：运载火箭减重1磅，节约发射资金40000美元！高速列车减重8%，综合节电17%！</span><br style="line-height:14.4px;white-space:normal;" />\r\n<br style="line-height:14.4px;white-space:normal;" />\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	先进镁合金 + 先进成型技术与装备 = 新工业时代的宠儿！\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<strong>项目简介：</strong>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	（1）通常采用单轴应力实验的断后延伸率来判断金属材料的锻造性能，但由于其应力状态（单轴）相对于实际的锻造（压缩、挤压、多向应力）来说过于简单，往往具有极大的局限性，而T-type可锻性测试是近年来提出的一种可以有效解决上述问题的一种新方法。Mg-Y基合金作为具有巨大潜力的高性能镁合金，目前为止，关于其可锻性，尤其是T-type可锻性的研究极少；\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n（2）T-type可锻性测试的应力状况比较接近实际的锻造，可以通过小样品试验来模拟实际的复杂锻造，本项目的研究数据，对模拟以及指导Mg-Y基合金优化锻造工艺，具有重要的指导意义。\r\n</p>\r\n<div style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</div>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<span style="line-height:1.5;white-space:nowrap;">研究内容包括但不限于：Y元素对镁合金可锻性能的影响；锻造温度对Mg-Y合金可锻性能的影响；不同变形条件下的显微组织。</span><span style="line-height:1.5;"></span>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<strong>联系方式：</strong>ahnydd@swjtu.edu.cn\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<strong>导师简介：</strong>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	http://userweb.swjtu.edu.cn/Userweb/ahnydd/index.htm\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n		  ', '尹冬弟', '2016-03-15 17:35:55', NULL),
(13, 'BD0AA36028B2709D', 'Mg-Y挤压合金的原位静态再结晶行为研究', '材料科学与工程学院\r', '\r\n		    <div style="line-height:14.4px;white-space:normal;text-align:center;">\r\n	<strong style="white-space:nowrap;line-height:1.5;">西南交大 交通运输装备轻量化研究所欢迎你！</strong>\r\n</div>\r\n<div style="line-height:14.4px;white-space:normal;text-align:center;">\r\n	<span style="line-height:1.5;">http://litmat.swjtu.edu.cn/</span>\r\n</div>\r\n<br style="line-height:14.4px;white-space:normal;" />\r\n<span style="line-height:14.4px;white-space:normal;">镁合金，最轻的高强度金属！借镁合金来创造--你可以的！</span><br style="line-height:14.4px;white-space:normal;" />\r\n<br style="line-height:14.4px;white-space:normal;" />\r\n<span style="line-height:14.4px;white-space:normal;">镁合金，密度1.75g/cm3 (铝合金2.76，钛4.5，钢7.8，铜8.9，铅11.3，金19.3)，是最轻的金属材料。而且镁合金永远不会资源枯竭！（铅，锌等已经枯竭；铜，铁，铝迅速枯竭！）</span><br style="line-height:14.4px;white-space:normal;" />\r\n<br style="line-height:14.4px;white-space:normal;" />\r\n<span style="line-height:14.4px;white-space:normal;">交通运输装备（车辆）要轻（轻量化）：运载火箭减重1磅，节约发射资金40000美元！高速列车减重8%，综合节电17%！</span><br style="line-height:14.4px;white-space:normal;" />\r\n<br style="line-height:14.4px;white-space:normal;" />\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	先进镁合金 + 先进成型技术与装备 = 新工业时代的宠儿！\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<strong>项目简介：</strong>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	（1）采用原位高温金相显微镜直接观察变形镁合金合金在不同条件下的静态再结晶行为；\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n（2）基于上述研究，调控变形镁合金的晶粒尺寸，从而调控其力学性能。\r\n</p>\r\n<div style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</div>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<span style="line-height:1.5;white-space:nowrap;">研究内容包括但不限于：Y元素、变形参数、退火工艺对变形镁合金显微组织和力学性能的影响规律。</span><span style="line-height:1.5;"></span>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<strong>联系方式：</strong>ahnydd@swjtu.edu.cn\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<br />\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	<strong>导师简介：</strong>\r\n</p>\r\n<p style="line-height:14.4px;white-space:normal;">\r\n	http://userweb.swjtu.edu.cn/Userweb/ahnydd/index.htm\r\n</p>\r\n		  ', '尹冬弟', '2016-03-15 17:32:58', NULL),
(14, 'A3D4DB5603C984C4', '保持码率的视频信息隐藏技术研究', '信息科学与技术学院\r', '\r\n		    <p style="text-indent:24pt;">\r\n	<span style="line-height:150%;font-family:宋体;font-size:12pt;">近年来，随着网络带宽的增加以及各种高效视频压缩算法的提出，数字视频的网络流量所占比重越来越多，俨然已成为网络上广为流行的主流媒体之一。作为大容量信息隐蔽通信的有效手段，视频信息隐藏技术已受到广泛关注。鉴于保持码率的视频信息隐藏</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">技术</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">更具有隐秘性，并有利于节省视频流传输带宽和存储空间，因此，更符合当前视频应用中安全、高效传输和存储的需求。针对现有视频信息隐藏技术还存在适用性受限、码率易扩张、隐藏容量有待于进一步扩展等问题，本项目拟结合主流视频压缩编码标准，</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">研究并实现<span>保持码率的视频信息隐藏</span>技术。</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">主要内容包括：（<span>1</span>）</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">在发送端将秘密信息嵌入数字视频中，且不影响视频的视觉观看质量和正常使用，然后把载有秘密信息的数字视频通过网络传输，在接收端将秘密信息从载密视频中提取出来，从而达到隐秘通信的目的；</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">（<span>2</span>）</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">如何在嵌入秘密信息时尽可能地保持视频流的码率基本不变，是保证载密视频安全高效传输和存储的一个重要问题</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">，并对视频质量、码率、隐藏容量、安全性等</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">性能进行分析。研究成果将为数字视频的安全应用和高效可靠的</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">隐蔽</span><span style="line-height:150%;font-family:宋体;font-size:12pt;">通信提供理论和技术支持。</span><span style="line-height:150%;font-family:宋体;font-size:12pt;"></span>\r\n</p>\r\n<br />\r\n<br />\r\n		  ', '王宏霞', '2016-03-15 15:40:25', NULL),
(15, 'B2A10D4D2F26F605', '高分遥感影像面向对象分类中的地物特征优选', '全校\r', '\r\n		    <p>\r\n	<span style="font-family:宋体;font-size:12pt;mso-bidi-font-family:&quot;Times New Roman&quot;;mso-font-kerning:1.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;">（<span lang="EN-US">1</span>）研究当前主流的，尤其是<span lang="EN-US">eCognition</span>软件所采用的一些地物特征的提取方法，会编程实现这些方法；</span>\r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:12pt;mso-bidi-font-family:&quot;Times New Roman&quot;;mso-font-kerning:1.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;">（<span lang="EN-US">2</span>）学习、理解面向对象分类方法；</span>\r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:12pt;mso-bidi-font-family:&quot;Times New Roman&quot;;mso-font-kerning:1.0pt;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA;">（<span lang="EN-US">3</span>）以国产高分卫星影像为例，研究一些典型地物的特征的相关性，并通过实验分析这些特征在面向对象分类方法中的重要性。</span>\r\n</p>\r\n		  ', '罗小军', '2016-03-15 15:13:07', NULL),
(16, 'A2785EE19F2FBB61', '世界大跨钢桁梁垮塌事故分析', '土木工程学院\r', '\r\n		    针对美国I-35W桥垮塌事故，采用大型有限元软件进行仿真分析，研究其垮塌过程和原因。\r\n		  ', '叶华文', '2016-03-15 12:06:24', NULL),
(17, '182A498FA70C3189', '铁路集装箱班列开行方案与定价优化', '全校\r', '\r\n		    欢迎有扎实的数学基础，运筹学成绩好，能够专心学习和研究的同学报名。单独报名，由老师来组队！\r\n		  ', '张小强', '2016-03-15 10:41:57', NULL),
(18, 'AEF1E4F28DC4CA2A', '基于百度地图的快递物流配送路径设计（适合SRTP项目，省创，或国创项目申请）', '全校\r', '\r\n		    <p>\r\n	GIS技术日益进步以及基于位置服务（LBS）平台的不断发展，为我们日常的出行带来了极大的方便，也为物流行业的发展尤其是运输、配送等方面的发展提供了很大的便利。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 目前，国内和国际的一些大型在线地图服务平台（如：谷歌地图、百度地图、高德地图等）均能够提供满足日常需求的位置服务、出行线路规划等基本服务。然而，在物流配送的过程中，除了需要利用位置服务和路径规划等基本的功能之外，还必须考虑配送成本的构成、车辆容量的限制以及客户需求量的大小等诸多因素，现有的在线地图服务平台均无法满足这方面的要求。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp; 但是，各在线地图供应商无一例外都提供了丰富的API接口供开发人员进行二次开发，以满足更加多样化的以及一些特殊的需求。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp; 本项目主要研究基于在线百度地图的、有容量限制的多点配送路径规划问题，主要涉及到在线数据的采集、路径的规划以及路径的可视化三个方面的内容，将丰富的在线地图数据和服务功能与配送路径规划之Clark-Wright节约法相结合，为GIS技术以及基于位置服务的丰富资源在物流领域的运用提供新的思路和借鉴。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp; 该项目可作为SRTP项目，省创，或国创项目申请，有兴趣的同学可与冯春教授联系&nbsp; <a href="mailto:ifengchun@swjtu.edu.cn">ifengchun@swjtu.edu.cn</a>\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	具体要完成的任务\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;①了解相关理论知识，包括WEBGIS和基于位置服务的最新技术、以及百度地图网站API调用方式等。\r\n</p>\r\n<p>\r\n	&nbsp;② 了解Clark-Wright节约法路径规划算法以及地理信息系统在物流领域的运用现状等。\r\n</p>\r\n<p>\r\n	&nbsp;③ 分析路径规划所需数据，并研究当前的在线地图能够提供哪些有效的数据，以及如何通过API接口获取所需的数据。\r\n</p>\r\n<p>\r\n	&nbsp; ④ 根据已有数据，与之Clark-Wright节约法相关进行数据交换，并将规划结果与地图相结合进行可视化。\r\n</p>\r\n<p>\r\n	&nbsp; ⑤ 第三四两个任务只是完成整个系统的两个步骤，但是不能分为两个系统，应该属于一个系统，即该系统应该同时具备在线数据采集、路径规划以及可视化的功能。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n		  ', '冯春', '2016-03-15 09:16:54', NULL),
(19, 'D797E6D6A8D1B33B', '不同环境温度下尼龙6单轴棘轮-疲劳交互作用研究', '力学与工程学院\r', '\r\n		    &nbsp;\r\n<p style="text-indent:18.7pt;">\r\n	<span style="font-family:宋体;">随着现代科学技术的发展，近年来已涌现出一大批高性能热塑性聚合物材料，如尼龙</span><span>6</span><span style="font-family:宋体;">。因其力学性能的显著提高而越来越多地用于</span><span style="font-family:宋体;line-height:115%;">工程结构构件以及尼龙绳等。这些构件一般都是在循环荷载下工作，有些构件甚至是工作在不同环境温度下，那么了解其在循环荷载作用下疲劳特性的研究将为结构安全及结构寿命的评定提供重要的实验数据。</span><span style="font-family:宋体;line-height:115%;">目前，</span><span style="font-family:宋体;line-height:115%;">对该聚合物材料已经较为全面地研究了其在不同相对含水率，不同加载水平下以及不同加载速率的单轴棘轮行为以及循环变形生热特性，同时也对其物理特性如：耐磨，耐腐蚀，抗老化进行了较为深入的研究，但是对不同环境温度下棘轮<span>-</span>疲劳交互作用还</span><span style="font-family:宋体;line-height:115%;">鲜见报导。这一研究的开展将为</span><span style="font-family:宋体;">优化该类构件的设计、提高其使用寿命和可靠性等来说是至关重要的。</span><span style="font-family:宋体;line-height:115%;">因此，本项目针对尼龙<span>6</span>材料在不同环境温度下开展较为系统的应力疲劳实验，讨论应力比，平均应力，应力幅值，加载速率以及环境温度诱发不同形态变形对棘轮<span>-</span>疲劳交互作用的影响。<span></span></span>\r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;line-height:115%;">由于尼龙<span>6</span>的玻璃化转变温度大概在<span>40-50</span>℃间，为此，本项目拟在尼龙<span>6</span>玻璃化转变温度区间，玻璃化转变温度以上以及玻璃化转变以下对其开展应力疲劳实验<span style="color:black;">。具体内容如下：<span></span></span></span>\r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;color:black;line-height:115%;">1.</span><span style="font-family:宋体;color:black;line-height:115%;">不同平均应力，不同应力幅值的单轴应力疲劳实验（含温度为<span>20,45</span>和<span>70</span>℃）；<span></span></span>\r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;color:black;line-height:115%;">2.</span><span style="font-family:宋体;color:black;line-height:115%;">相同峰值应力，不同应力比的单轴应力疲劳实验（含温度为<span>20,45</span>和<span>70</span>℃）；<span></span></span>\r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;color:black;line-height:115%;">3.</span><span style="font-family:宋体;color:black;line-height:115%;">不同应力率的单轴应力疲劳实验（含温度为<span>20,45</span>和<span>70</span>℃）；<span></span></span>\r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;color:black;line-height:115%;">4.</span><span style="font-family:宋体;color:black;line-height:115%;">不同应力循环实验过程中试样表面温度的测量。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;">学习软件：</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">Origin</span><span style="font-size:9pt;">，</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">Excel</span><span style="font-size:9pt;">，</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">Word</span><span style="font-size:9pt;">和</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">Powerpoint</span><span style="font-size:9pt;">。</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;">人数：</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">4</span><span style="font-size:9pt;">人，可在后续的毕业设计中延续该项研究。</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"></span>\r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;color:black;line-height:115%;"><span></span></span>&nbsp;\r\n</p>\r\n		  ', '康国政', '2016-03-14 23:03:02', NULL),
(20, '2AD388D88C463232', '接缝传力杆错位对混凝土路面结构响应的影响分析', '土木工程学院\r', '\r\n		    <span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;line-height:150%;text-indent:21pt;mso-char-indent-count:2.0;tab-stops:220.5pt;">\r\n	<span style="font-size:small;"><span style="line-height:150%;font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;;">本项目通过建立设置传力杆接缝的混凝土路面结构模型，通过数值计算，分析</span><span style="line-height:150%;font-family:宋体;mso-bidi-font-size:10.5pt;">接缝错位对混凝土路面结构的响应，主要是对路面临界荷位处应力的影响，以及引起的混凝土板设计厚度和疲劳寿命变化</span><span style="line-height:150%;font-family:宋体;mso-bidi-font-size:10.5pt;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-hansi-font-family:&quot;Times New Roman&quot;;">。</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n		  ', '周正峰', '2016-03-14 21:23:44', NULL),
(21, 'D3B6AD77F1423C8B', '汽车移动荷载作用下的路面结构响应分析', '土木工程学院\r', '\r\n		    <span style="font-family:宋体;font-size:small;"> </span><span style="font-size:small;"><span style="line-height: 150%; font-family: 宋体; mso-bidi-font-size: 10.5pt;" roman";"="" new="" roman";mso-hansi-font-family:"times="" times="">本项目通过建立沥青路面结构模型，通过数值计算，分析</span><span style="line-height:150%;font-family:宋体;mso-bidi-font-size:10.5pt;">汽车移动荷载作用下的路面结构响应，包括</span><span style="line-height: 150%; font-family: 宋体; mso-bidi-font-size: 10.5pt;" roman";"="" new="" roman";mso-hansi-font-family:"times="" times="">路表弯沉、面层层底拉应力和基层层底拉应力等路面力学响应量的变化规律，并与静载进行对比分析。</span></span><span style="font-family:宋体;font-size:small;"> </span>\r\n		  ', '周正峰', '2016-03-14 21:19:27', NULL),
(22, 'F32506D73DB60C00', '细水雾扑灭食用油火的实验研究', '地球科学与环境工程学院\r', '\r\n		    <p align="left">\r\n	<span style="font-size:12.0pt;line-height:150%;font-family:宋体;">近年来，随着高效高能炊具和高自燃点食用油的广泛应用，增加了厨房区域的火灾危险性。统计数据显示，酒店和快餐店的火灾有</span><span style="font-size:12.0pt;line-height:150%;">50%</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">起源于厨房，主要燃料为食用油类。食用油类火灾扑救较为困难。常见的灭火剂如干粉、二氧化碳等由于冷却能力不足，灭火后极易发生复燃。现在一般采用的湿式化学灭火剂虽然能有效灭火，但存在灾后清理困难和成本较高的问题。细水雾无毒环保，灾后易于清理，具有卓越的冷却效果。但设计参数非常重要，如果系统设计不合理，则不能达到预期的灭火效果。本项目在已搭建的实验平台上，利用已建成的细水雾灭火系统，开展细水雾扑灭食用油火的有效性研究。<span style="font-family:宋体;font-size:16px;line-height:24px;text-indent:32px;white-space:normal;">有兴趣的同学可以通过QQ2081587585或邮箱yuebao201@126.com 联系</span></span><span style="font-size:12.0pt;line-height:150%;"></span> \r\n</p>\r\n		  ', '刘静', '2016-03-14 20:50:26', NULL);
INSERT INTO `srtphire` (`id`, `oid`, `title`, `orienting`, `content`, `author`, `original_time`, `breviary`) VALUES
(23, '89AB8FD5B36061E4', '                          水喷雾扑灭油池火的效果研究', '地球科学与环境工程学院\r', '\r\n		    <span lang="ZH-CN" style="font-size:12pt;font-family:宋体;">城市隧道的安全运营越来越受到社会的广泛关注。然而隧道自身的特殊性使得隧道内的火灾发生率极高，并且一旦发生火灾，损失极大。为了防止隧道火灾发生，必须采取必要的交通措施和完善的消防系统设计。近年来，在一些车流量大、性质重要的隧道内开始采用泡沫</span><span style="font-size:12pt;font-family:''Times New Roman'', serif;">_</span><span lang="ZH-CN" style="font-size:12pt;font-family:宋体;">水喷雾联用灭火系统。相比传统喷淋系统，由大量雾状小水滴组成的水喷雾，能迅速吸热降温，在短时间内便可迅速控制和扑灭火灾，节省大量灭火用水。其降低环境温度的作用尤其明显，对隧道结构可以起到很好的保护，也利于消防人员接近火场。但也存在一些不尽人意的地方，如有些火灾发生在车辆下部或车厢内部，顶部作用的喷水可能起不到很好的效果。在该项目中，将通过已经搭建的模型平台，开展试验验证水喷雾扑灭油池火的性能，并重点考虑遮挡条件下的灭火效果。<span style="font-family:宋体;font-size:16px;line-height:24px;text-indent:32px;white-space:normal;">有兴趣的同学可以通过QQ2081587585或邮箱yuebao201@126.com 联系</span></span>\r\n		  ', '刘静', '2016-03-14 20:22:56', NULL),
(24, '422ACAF1814B6647', '城郊乡村公路的噪声环境影响预测', '全校\r', '\r\n		    <span style="font-family:宋体;font-size:small;"> </span><span style="font-family:宋体;font-size:small;">\r\n<p style="margin:0cm 0cm 0pt;text-indent:24pt;">\r\n	<span style="font-family:宋体;mso-bidi-font-family:&quot;Times New Roman&quot;;">由于现今颁布的关于噪声环境影响的预测模型均针对高速公路、城市快速路等车速快、车流量大项目的噪声预测，而在应用于位于人口相对稀少、车流量相对较小的城郊乡村公路时会受到一定的限制，预测结果往往与实际结果有较大出入，但实际上由于城郊乡村公路遍布全国各地，在公路网中占很大比例，因此，确定适合该类型公路的噪声预测模型、增加噪声预测的准确度、指导城市规划布局是非常必要的。</span>\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;text-indent:24pt;">\r\n	<span style="font-family:宋体;mso-bidi-font-family:&quot;Times New Roman&quot;;">学生需要对附近乡村公路进行实地监测，并结合现场环境条件调查，对比、分析模型预测的噪声值与实际监测值之间的差异大小，找出可能影响预测值的主要因素，并提出修正意见和建议。</span>\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;text-indent:24pt;">\r\n	<br />\r\n</p>\r\n</span>\r\n<p style="margin:0cm 0cm 0pt;text-indent:24pt;">\r\n	<br />\r\n</p>\r\n		  ', '吴文娟', '2016-03-14 20:11:15', NULL),
(25, '7E723EABF4EEB7E6', '水幕在隧道内防火分隔效果的实验研究', '地球科学与环境工程学院\r', '\r\n		    <p align="left" style="text-indent:24pt;">\r\n	<span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:windowtext;">随着隧道数量及交通流量的与日俱增，隧道火灾安全问题成为人们关注的热点。</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;color:windowtext;">由于隧道的相对封闭性，一旦发生火灾并得不到有效控制，将造成巨大的损失和产生重大的社会影响。</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">如果沿隧道长度方向每隔一定距离在隧道的横断面上设置一幅消防水幕，将隧道划分成若干个防火单元。发生火灾时，起火区域两侧的水幕系统动作，喷头开启喷水形成“水墙”，从而将着火区与其余空间隔离开来。由于水幕具有冷却作用，可以衰减热辐射，吸收来自着火区的热量，因此可以阻止火势蔓延扩大，降低水幕另一侧隧道内的温度；同时，水幕能起到挡烟的作用，延缓烟气在隧道内的蔓延速度，并对烟气有清洗作用，从而可以改善水幕另一侧的能见度；此外，水幕可以吸收或稀释火灾产生的有毒气体，进而降低水幕另一侧的毒气浓度。从而在水幕的另外一侧形成一个相对安全的“受保护区域</span><span style="font-size:12.0pt;line-height:150%;">”</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">，使“受保护区域</span><span style="font-size:12.0pt;line-height:150%;">”</span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">的环境条件得到改善</span><span style="font-size:12.0pt;line-height:150%;"> </span><span style="font-size:12.0pt;line-height:150%;font-family:宋体;">为人员的安全疏散赢得更多的时间，同时也有利于救援人员接近火源。但水幕在实际应用中的防火分隔效果需要通过实验进行检验。本课题通过在已经搭建的模型平台上，研究水幕的配置参数对其防火分隔效果的影响。有兴趣的同学可以通过QQ2081587585或邮箱yuebao201@126.com 联系</span><span style="font-size:12.0pt;line-height:150%;"></span> \r\n</p>\r\n		  ', '刘静', '2016-03-14 19:29:33', NULL),
(26, '4B53748B1066FC59', '基于结构光技术的物体轮廓测量研究', '电气工程学院\r', '\r\n		    <p align="left">\r\n	&nbsp;&nbsp;&nbsp; 结构光测量技术是目前机器视觉领域里较为常用的一种高精度测量技术，通过采用大分辨率面阵相机及线光源，借助一定的数学变换和标定方法，可以实现客观世界实际物体尺寸、外观、距离等物理几何的精确测量，航空航天、轨道交通等诸多领域有非常广泛的应用。本项目的具体工作任务如下：\r\n</p>\r\n<ol>\r\n	<li>\r\n		了解结构光检测的基本原理，包括常用的标定方法、相机参数求取等；\r\n	</li>\r\n	<li>\r\n		能够利用面阵相机、线结构光（拟通过项目费用购置），搭建结构光标定实验平台；\r\n	</li>\r\n	<li>\r\n		选择正确的标定方法，运用VC软件，编写结构光测量系统软件程序，进而构建结构光测量系统；\r\n	</li>\r\n	<li>\r\n		利用搭建的结构光测量系统，测量实际的物理几何参量，制作结构光传感器。\r\n	</li>\r\n</ol>\r\n<p>\r\n	附件介绍了目前结构光测量技术在轨道交通测量中的应用。\r\n</p>\r\n		  ', '于龙', '2016-03-14 19:23:21', NULL),
(27, 'DF0D62B0F39EB7EC', '政策缝隙与社会风险', '公共管理学院\r', '\r\n		    这是一个有一定理论思辨性的选题，需要学生较好的文献能力和文字能力。\r\n		  ', '赵成', '2016-03-14 17:11:57', NULL),
(28, '86F75635146A9692', 'SRTP项目：对制度的信任能否降低环境污染风险的感知？', '全校\r', '\r\n		    <p>\r\n	申请校级或者国家级SRTP项目。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n利用教师提供的数据，对于中国公众的制度信任与环境风险感知之间的关系进行分析，并对此进行理论讨论。 研究成果质量如果较好，可发表在SSCI类刊物。 选题要求： 本研究需要投入较多的时间精力，请慎重选择。需要阅读英文文献，具备一定的统计分析能力，掌握spss，stata，R等统计软件之一。\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	联系邮箱，请附上简历：\r\n</p>\r\n<p>\r\n	<a href="mailto:norsky@163.com">norsky@163.com</a>\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n		  ', '王思琦', '2016-03-14 17:11:35', NULL),
(29, '752C962AF2BDEE95', '环境风险感知的文化差异', '公共管理学院\r', '\r\n		    <p style="text-align:left;text-indent:21pt;background:#ffffff;">\r\n	<span style="font-family:宋体;background:#ffffff;color:#333333;font-size:12pt;">我们的研究就是根据<span>CGSS</span><span>数据作为实证资料，来分析与环境、风险等相关的主题。</span></span><span style="font-family:Arial;background:#ffffff;color:#333333;font-size:12pt;"></span>\r\n</p>\r\n<p style="text-align:left;text-indent:21pt;background:#ffffff;">\r\n	<span style="font-family:宋体;background:#ffffff;color:#333333;font-size:12pt;">许多因素可能会影响一个人的环境行为，例如教育、收入、文化等。本研究利用<span>CGSS</span><span>数据对此进行验证。</span></span><span style="font-family:宋体;background:#ffffff;color:#333333;font-size:12pt;"></span>\r\n</p>\r\n		  ', '赵成', '2016-03-14 17:09:23', NULL),
(30, 'F1D7DA6494F8DEF9', '“慕课”平台在线讨论区的设计', '全校\r', '\r\n		    <p>\r\n	&nbsp;&nbsp;&nbsp; 本项目拟对“慕课”平台的在线讨论区进行设计。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 要求参加本项目的同学要有一定的。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; 联系电话：13980785972。\r\n</p>\r\n		  ', '龚晖', '2016-03-14 17:08:34', NULL),
(31, 'E18037BB4F91CE9F', 'SRTP项目：大学生如何看待思政课？来自IAT方法的经验证据', '全校\r', '\r\n		    <p>\r\n	申请SRTP校级或者国家级项目。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	思政课对于大学生国家意识有重要影响。大学生如何看待思政课？那些因素会影响大学生对思政课的态度和认知？作为下一步大规模实地实验方法field experiment的前期研究，本研究项目需要采用访谈、问卷等方法对此进行初步研究，可以结合内隐联想测验IAT（Implicit Association Test）方法，以测量潜在偏好的影响。研究过程中，教师会提供经费和其他帮助。 如果研究质量较好，可以发表在SSCI类刊物。 选题要求，项目需要投入较多的时间精力，进行实地调查，请慎重选择。需要阅读英文文献，具备定性和定量研究的基本能力。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	联系邮箱，请附上简历\r\n</p>\r\n<p>\r\n	<a href="mailto:norsky@163.com">norsky@163.com</a> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n		  ', '王思琦', '2016-03-14 17:06:39', NULL),
(32, '8D8897C844E5C8E3', 'SRTP项目：中国公众环保知识掌握程度的变迁及其影响因素', '全校\r', '\r\n		    <p>\r\n	申请校级或者国家级SRTP项目。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	利用教师提供的调查数据，使用统计方法，分析中国公众近十年来环境保护知识掌握程度的变化趋势，并且提出理论解释与对策建议。 研究成果在达到一定质量的前提下，拟发表在SCI类刊物。 选题要求： 项目需要投入较多的时间精力，请慎重选择，建议出国留学或者保研的同学选择。需要阅读英文文献，具备一定的统计分析能力，掌握spss，stata，R等统计软件之一。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	联系邮箱，请附上简历。\r\n</p>\r\n<p>\r\n	<a href="mailto:norsky@163.com">norsky@163.com</a> \r\n</p>\r\n		  ', '王思琦', '2016-03-14 17:02:04', NULL),
(33, '9216E4B18F7CFC83', '高墩桥梁抗震性能的振动台试验研究', '土木工程学院\r', '\r\n		    <p>\r\n	了解桥梁结构主要震害及设防措施，掌握混凝土构件振动台试验的流程及特点，初步了解结构动力分析的理论和方法。配合课题组研究人员完成试验过程中震损现象的观测、记录工作，协助研究团队进行试验数据的整理和分析工作。\r\n</p>\r\n		  ', '邵长江', '2016-03-14 16:46:00', NULL),
(34, '2424A02CB72437D5', '高墩桥梁抗震性能的拟静力试验研究', '土木工程学院\r', '\r\n		    <p>\r\n	了解桥梁结构主要震害及设防措施，掌握混凝土构件拟静力试验的流程及特点。配合课题组研究人员完成试验过程中震损现象的观测、记录工作，协助研究团队进行试验数据的整理和分析工作。\r\n</p>\r\n		  ', '邵长江', '2016-03-14 16:45:01', NULL),
(35, '6227F9915A396BD9', '容性电气设备在线监测用高精度微电流传感器设计 ', '全校\r', '\r\n		    <span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">在线监测系统中信号获取是通过传感器从电气设备上反映设备状态的物理量和化学量并将其转换为电信号，其中传感器作为获取信号的重要装置起着至关重要的作用。信号中是否包含准确反映设备状态的数据决定了后续针对设备运行状态的分析是否合理和可靠，从而影响检修的决策。电流传感器作为在线监测中获取泄露电流的关键装置，提高其灵敏度、精度和稳定性等对提升在线监测的效果具有重要的意义。<span lang="EN-US">&nbsp;&nbsp;</span></span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">学生应完成的任务：</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">1</span>）查阅资料，掌握泄漏电流的基本理论知识、测试原理与测试方法。</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">2</span>）选择合适的传感器铁心，设计泄漏电流测量微电流传感器。</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">3</span>）设计信号调理及数据采集电路，完成系统调试，实现系统功能。</span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">4</span>）完成项目总结报告，发表学术论文。 </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">5</span>）完成项目结题答辩<span lang="EN-US">PPT</span>，准备答辩。 </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">指导老师信息：高波，<span lang="EN-US">Email</span>：<span lang="EN-US"><a href="mailto:bogao@home.swjtu.edu.cn"><span style="color:#0000ff;">bogao@home.swjtu.edu.cn</span></a></span>；<span lang="EN-US">QQ</span>：<span lang="EN-US">179163015</span></span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n		  ', '高波', '2016-03-14 16:09:55', NULL),
(36, 'FE3FB6CB85424E6F', '基于介质响应法的发电机主绝缘多因子老化特性研究', '全校\r', '\r\n		    <span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">大型发电机是发电系统中最重要和最关键的电气设备，<span lang="EN-US">600MW</span>和<span lang="EN-US">1000MW</span>发电机组已经成为电网的主力机组，其运行可靠性直接关系到整个系统的安全与稳定运行。主绝缘是发电机最重要的组成部分，也是最为薄弱的环节，大多数发电机故障都是由主绝缘缺陷引起的。在长期运行过程中，由于受到电场、热场、机械应力和环境因素等诸多因子的联合作用，其电气性能随运行年限不断下降，击穿电压逐步降低，最终由于主绝缘老化导致击穿，引发停机事故，造成重大经济损失。 </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="font-family:宋体;"><span style="line-height:150%;font-size:10pt;">目前的大型发电机主绝缘状态检测仍主要依赖绝缘电阻测试、介质损耗正切测试、交直流耐压试验、局部放电测量等传统方法。这些方法均属于宏观性能测试手段，在发电机绝缘故障检测方面发挥了一定的作用，但在实际应用中也存在一些问题：（<span lang="EN-US">1</span>）有些试验施加的电压较高，容易在试验过程中对发电机主绝缘造成损伤，减少发电机的使用寿命。（<span lang="EN-US">2</span>）这些检测方法局限于单一信息，提供的信息量少，而且所受的干扰较多，测试结果分散性大，很难全面准确的诊断发电机主绝缘状态。因此，为了提高发电机运行的可靠性，迫切需要研究新的检测手段，对发电机主绝缘老化状态检测及评估技术进行系统深入研究，以保障其安全稳定运行。<span lang="EN-US">&nbsp;</span></span><span lang="EN-US" style="line-height:150%;font-size:10.5pt;"> </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">项目研究内容： </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">本项目具有一定的探索性，旨在探寻评估大型发电机主绝缘老化状态的新理论和新方法。 </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">1</span>）搭建发电机主绝缘定子线棒电<span lang="EN-US">-</span>热<span lang="EN-US">-</span>振动老化试验平台，进行不同条件下的联合老化试验； </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">2</span>）通过试验研究不同老化状态下发电机定子线棒的介电响应特性； </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">3</span>）建立介电响应特征参数与发电机主绝缘状态之间的映射关系； </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">4</span>）实现发电机主绝缘状态全面准确评； </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">5</span>）完成项目研究报告及总结报告，发表学术成果； </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">6</span>）完成项目结题答辩<span lang="EN-US">PPT</span>，进行项目答辩。 </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">指导老师信息：高波，<span lang="EN-US">Email</span>：<span lang="EN-US"><a href="mailto:bogao@home.swjtu.edu.cn"><span style="color:#0000ff;">bogao@home.swjtu.edu.cn</span></a></span>；<span lang="EN-US">QQ</span>：<span lang="EN-US">179163015</span></span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n		  ', '高波', '2016-03-14 16:09:29', NULL),
(37, 'E90F00400E51C109', '基于DSP的机车粘着控制系统设计', '电气工程学院\r', '\r\n		    <p>\r\n	<strong>背景</strong>：<br />\r\n机车的牵引和制动依赖于其轮轨间的粘着，确切的说，列车牵引力是通过轮轨接触面的蠕滑而提供的，伴随蠕滑产生粘着力，轮轨之间才能传递切向牵引力。若牵引力或制动力大于轮轨间可用粘着力，则会出现空转或滑行，制约牵引力或制动力的发挥，同时会引发如擦轮现象、齿轮传动非线性耦合振动或动力轮对服役安全性问题。\r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;"><strong>目的</strong>：</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;">针对高速列车及重载机车的粘着机理及控制策略进行研究，设计粘着控制方法，并通过</span><span style="line-height:1.5;">DSP</span><span style="line-height:1.5;">实现控制方法。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong>实验手段及工具</strong>：\r\n</p>\r\n<p style="margin-left:18pt;">\r\n	1. Matlab/Simulink\r\n</p>\r\n<p style="margin-left:18pt;">\r\n	2. SIMPACK动力学仿真软件\r\n</p>\r\n<p style="margin-left:18pt;">\r\n	3. DSP28335\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong>团队</strong>：4-5人\r\n</p>\r\n<p>\r\n	<strong>级别</strong>：拟申请省级项目。\r\n</p>\r\n<p>\r\n	Email:songwang@swjtu.edu.cn\r\n</p>\r\n<p>\r\n	QQ: 1007492682\r\n</p>\r\n<p>\r\n	<span></span> \r\n</p>\r\n		  ', '王嵩', '2016-03-14 16:09:05', NULL),
(38, '8FFE735459DEA0A9', '基于大数据分析的变压器状态评估技术研究', '电气工程学院\r', '\r\n		    <span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">电力变压器是电能转换和传输的核心，是电力系统中最重要和最关键的设备，提高变压器的运行可靠性对保障电力系统安全运行具有重要意义。提高变压器运行可靠性除了采用质量好的变压器之外，还应不断提高变压器的运行、维护及检修水平。目前电气设备的检修模式逐渐向状态检修转变，实践证明状态检修对提高设备检修质量、降低检修费用、延长设备使用寿命、发挥设备的最大经济效益具有重要意义。要实现变压器的状态检修首先要能正确评估其运行状态。随着状态检修工作开展的不断深入，各电力公司都积累了大量变压器在线监测和离线试验数据，如何发挥这些数据的作用，基于变压器在线监测和离线试验数据实现变压器运行状态的有效评估是一个重要的研究课题。 </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;line-height:150%;text-indent:24.1pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">研究内容： </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;line-height:150%;text-indent:24.1pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">1</span>）搜集变压器运行数据，包括：离线试验数据和在线监测数据，并对数据进行预处理； </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;line-height:150%;text-indent:24.1pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">2</span>）确定变压器运行状态主要特征参量，初步建立状态评估指标体系； </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;line-height:150%;text-indent:24.1pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">3</span>）确定变压器运行状态的评估方法，对变压器运行状态进行准确评估； </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;line-height:150%;text-indent:24.1pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">4</span>）结合实例验证评估方法的有效性。 </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;line-height:150%;text-indent:24.1pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">5</span>）完成项目研究报告及总结报告，发表学术成果； </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="margin:0cm 0cm 0pt;line-height:150%;text-indent:24.1pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">（<span lang="EN-US">6</span>）完成项目结题答辩。 </span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n<p style="line-height:150%;text-indent:24pt;">\r\n	<span style="line-height:150%;font-size:10pt;"><span style="font-family:宋体;">指导老师信息：高波，<span lang="EN-US">Email</span>：<span lang="EN-US"><a href="mailto:bogao@home.swjtu.edu.cn"><span style="color:#0000ff;">bogao@home.swjtu.edu.cn</span></a></span>；<span lang="EN-US">QQ</span>：<span lang="EN-US">179163015</span></span></span>\r\n</p>\r\n<span style="font-family:宋体;font-size:small;"> </span>\r\n		  ', '高波', '2016-03-14 16:08:57', NULL),
(39, '123236745C1F2C6D', '犀浦校区明湖水中重金属元素含量的分析', '全校\r', '\r\n		    &nbsp;\r\n<p>\r\n	<span style="font-size:12pt;font-family:宋体;line-height:150%;">&nbsp;&nbsp;&nbsp; 水是人体的重要组成部分，水的质量则直接影响着人体的健康。随着工业的发展，工业废水的排放逐渐增多，还有未经处理的生活用水的直接排放，都会加剧水质的污染程度。为了应对日益严重的水污染状况，人们必须有效、及时地掌握水质信息，增强水质监测的可靠性和科学性。本研究按国家标准采用火焰原子吸收光谱法对犀浦校区明湖中的湖水重金属含量进行检测，评估犀浦校区湖水中重金属的含量情况，进而评价水质状况。</span>\r\n</p>\r\n<p style="text-indent:24pt;">\r\n	<span style="font-size:12pt;font-family:宋体;line-height:150%;"></span><span style="font-family:仿宋_GB2312;line-height:150%;"></span>&nbsp;\r\n</p>\r\n		  ', '陈钰', '2016-03-14 14:04:12', NULL),
(40, 'DD76C71C7DEFDA6A', '酸度对火焰光度计测定食品中钠含量的影响', '全校\r', '\r\n		    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="font-size:14pt;font-family:宋体;">原子吸收火焰分光光度计是一种常用的分析仪器，广泛应用于国防工业生产、化学分析研究及医药产品开发等领域的分析测试。火焰光度分析技术在近几十年中得到快速的发展，形成了很多很好的具体检测方法，具有快速、简便、经济等特点。原子吸收火焰分光光度计也正因为具有这些优点而被广泛采用。<span></span></span> \r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:14pt;font-family:宋体;"><span>&nbsp;&nbsp;&nbsp; </span></span><span style="font-size:14pt;font-family:宋体;">目前，我国食品安全国家标准《<span>GB/T5009</span>．<span>91-2003</span>食品中钠的测定》中规定原子吸收火焰分光光度法作为唯一方法，适用于测定各种食品中的钠元素含量。由此可见，火焰光度计在我国现代食品检测中的重要作用，但在实际工作中常遇到前处理后的样品测试液酸度较大的情况，本研究就此情况分析酸度对火焰光度计测定钠的影响程度。<span></span></span>\r\n</p>\r\n		  ', '陈钰', '2016-03-14 13:55:35', NULL),
(41, '80C082341FB5454B', '酸度对火焰光度计测定食品中钾的影响', '地球科学与环境工程学院\r', '\r\n		    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="font-size:14pt;font-family:宋体;">原子吸收火焰分光光度计是一种常用的分析仪器，广泛应用于国防工业生产、化学分析研究及医药产品开发等领域的分析测试。火焰光度分析技术在近几十年中得到快速的发展，形成了很多很好的具体检测方法，具有快速、简便、经济等特点。原子吸收火焰分光光度计也正因为具有这些优点而被广泛采用。<span></span></span> \r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:14pt;font-family:宋体;"><span>&nbsp;&nbsp;&nbsp; </span></span><span style="font-size:14pt;font-family:宋体;">目前，我国食品安全国家标准《<span>GB/T5009</span>．<span>91-2003</span>食品中钾的测定》中规定原子吸收火焰分光光度法作为唯一方法，适用于测定各种食品中的钾元素含量。由此可见，火焰光度计在我国现代食品检测中的重要作用，但在实际工作中常遇到前处理后的样品测试液酸度较大的情况，本研究就此情况分析酸度对火焰光度计测定钾的影响程度。<span></span></span>\r\n</p>\r\n		  ', '陈钰', '2016-03-14 13:54:01', NULL),
(42, '463B5F9ED142BEBE', '单相电力锁相环的研究与实现', '全校\r', '\r\n		    实现单相电力锁相的功能，并进行测试\r\n		  ', '熊成林', '2016-03-14 13:51:20', NULL),
(43, '99827BFDDAFF84CC', '永磁无刷直流电机驱动小车设计与实现', '电气工程学院\r', '\r\n		    <p>\r\n	采购相关部件材料，采用单片机或者DSP编程，实现电动小汽车的驱动控制实现，拟申请国创项目\r\n</p>\r\n		  ', '葛兴来', '2016-03-14 12:22:28', NULL),
(44, 'B942A35E91F2750C', '电动汽车用永磁无刷直流电机驱动控制系统仿真设计', '电气工程学院\r', '\r\n		    <p>\r\n	基于MATLAB的 Simulink和Stateflow工具，以永磁无刷直流电机为驱动轮毂电机，完成电动汽车驱动控制系统的仿真建模、运行控制和GUI设计。拟申请一般SRTP项目。\r\n</p>\r\n		  ', '葛兴来', '2016-03-14 11:54:15', NULL),
(45, 'CC058F6886207AD9', 'SRTP2016快递自提点现状及标准化分析', '交通运输与物流学院\r', '\r\n		    <p>\r\n	感兴趣者可通过email：<a href="mailto:migan@swjtu.cn报名。">migan@swjtu.cn报名。</a> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	招募者：甘蜜副教授，西南交通大学交通运输与物流学院，国家自然科学基金等多个基金获得者，指导国家节能减排大赛获二等奖，指导研究生获联合国环规署绿苗机会奖，四川循环经济年会二等奖\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n&nbsp;<span style="color:black;font-size:12pt;">“最后一公里”服务已成为快递业发展的瓶颈，也是国家发改委十分关心的时下热点问题，对物流配送自提点的建设和服务进行规范化、</span><span style="font-family:宋体;color:black;font-size:12pt;"> </span><span style="color:black;font-size:12pt;">标准化能增加快递终端配送效益</span><span style="font-family:宋体;color:black;font-size:12pt;">,</span><span style="color:black;font-size:12pt;">提高社区居民服务满意度。本项目的实施以科学发展观为指导，把不断满足人民群众日益增长和不断丰富的快递使用需求作为出发点，充分发挥市场机制的作用，释放企业的积极性和创造性。切实解决快递末端投递中存在的突出问题，促进快递行业又好又快发展，减少快递服务中因快递自提点建设不规范所导致的货损、货差所导致的服务质量问题，减少质量纠纷，通过建设与服务规范的提出，给快递企业物流自提点的建设提供相关依据，促使快递最后一公里服务质量提高，效率的提升。</span><span style="font-family:宋体;color:black;font-size:12pt;"></span> \r\n<p>\r\n	<span style="font-family:宋体;color:black;font-size:12pt;"><span>&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-family:方正仿宋简体;color:black;font-size:12pt;">加上现今邮政快递自提柜的建设已有一定的标准可采纳，作为社区最后一公里中更为普遍的快递自提点相关标准几乎为空白，因此本项目的实施具备迫切性。</span> \r\n</p>\r\n<p>\r\n	<span style="font-family:方正仿宋简体;color:black;font-size:12pt;">完成任务：&nbsp;\r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-family:宋体;font-size:12pt;"><span>&nbsp;</span></span><span style="font-size:12pt;">（</span><span style="font-family:宋体;font-size:12pt;">1</span><span style="font-size:12pt;">）物流自提点的现状分析</span><span style="font-family:宋体;font-size:12pt;"></span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-family:宋体;font-size:12pt;"><span>&nbsp;&nbsp; </span><span>&nbsp;&nbsp;</span></span><span style="font-size:12pt;">通过问卷设计和现场走访的形式，对物流自提点的现状和存在的问题进行调研和分析，梳理物流自提点服务内容和服务流程。</span><span style="font-family:宋体;font-size:12pt;"></span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-family:宋体;font-size:12pt;"><span>&nbsp;&nbsp;&nbsp; </span></span><span style="font-size:12pt;">（</span><span style="font-family:宋体;font-size:12pt;">2</span><span style="font-size:12pt;">）社区居民快递需求调查问卷设计和调查</span><span style="font-family:宋体;font-size:12pt;"></span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-family:宋体;font-size:12pt;"><span>&nbsp; </span><span>&nbsp;&nbsp;</span><span>&nbsp;</span></span><span style="font-size:12pt;">通过网络调研，资料收集和发放问卷的办法调研成都市部分社区居民，获取居民对快递服务最后一公里的需求和现存主要问题。</span><span style="font-family:宋体;font-size:12pt;"></span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-family:宋体;font-size:12pt;"><span>&nbsp;&nbsp;&nbsp; </span></span><span style="font-size:12pt;">（</span><span style="font-family:宋体;font-size:12pt;">3</span><span style="font-size:12pt;">）建设和服务标准编制</span><span style="font-family:宋体;font-size:12pt;"></span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-family:宋体;font-size:12pt;"><span>&nbsp;&nbsp;&nbsp; </span><span>&nbsp;</span></span><span style="font-size:12pt;">根据标杆管理办法，依据标杆型自提点的情况，设计物流自提点的建设与服务规范。</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	&nbsp;\r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	&nbsp;\r\n</p>\r\n</span>\r\n		  ', '甘蜜', '2016-03-14 11:30:46', NULL),
(46, 'B571F87058064CC8', 'SRTP2016：可获取物流开源大数据采集、调研和分析', '全校\r', '\r\n		    <p>\r\n	尽管大数据分析已成为时下热门，但物流大数据的获取仍存在大量的不足。\r\n</p>\r\n<p>\r\n	本项目拟建立数据平台，通过对可获取的开源数据源进行检索，开源数据提取方法进行分析，设计数据库结构，对采取的数据进行存储、分类和解析。对相关数据进行梳理、分析，分析数据之间相互影响机理等，并展望数据的运用方向。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	感兴趣者可通过email：<a href="mailto:migan@swjtu.cn报名。">migan@swjtu.cn报名。</a> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	招募者：甘蜜副教授，西南交通大学交通运输与物流学院，国家自然科学基金等多个基金获得者，指导国家节能减排大赛获二等奖，指导研究生获联合国环规署绿苗机会奖，四川循环经济年会二等奖\r\n</p>\r\n		  ', '甘蜜', '2016-03-14 11:09:26', NULL),
(47, 'F47348249B8096DC', 'SRTP2016：Uber、滴滴等对区域交通拥堵影响研究', '交通运输与物流学院\r', '\r\n		    <p>\r\n	专车的发展，给人们出行带来了较大的便利，刺激了出行需求。越来越多的专家从宏观层面提出专车将加剧交通拥堵，本项目拟从微观层面出发，选定成都市某区域进行调查，结合开源数据的获取，来分析专车与交通拥堵之间的关系，并提出相应的优化建议。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	招募感兴趣的同学进行SRTP实践研究。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	可与<a href="mailto:migan@swjtu.cn联系。">migan@swjtu.cn联系。</a> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	招募者：甘蜜副教授，西南交通大学交通运输与物流学院，国家自然科学基金等多个基金获得者，指导国家节能减排大赛获二等奖，指导研究生获联合国环规署绿苗机会奖，四川循环经济年会二等奖\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n		  ', '甘蜜', '2016-03-14 10:59:50', NULL),
(48, '8A6DE1F52859926F', '具有太阳能能量收集与应用功能的太阳伞', '全校\r', '\r\n		    设计具有太阳能能量收集功能的太阳伞，并将其进行利用，提升太阳伞使用者的舒适度。\r\n		  ', '熊成林', '2016-03-14 09:08:03', NULL),
(49, 'B0BDB27715D7D2AB', '电流型LED驱动器设计', '电气工程学院\r', '\r\n		    根据LED的工作特性，设计出满足LED驱动特性的电流型开关电源并进行测试\r\n		  ', '熊成林', '2016-03-14 08:52:05', NULL),
(50, '575D2DC720F111FC', '高铁桥上全封闭声屏障的降噪效果试验研究', '全校\r', '\r\n		    <p style="text-indent:21.0pt;">\r\n	<span style="font-family:宋体;">声屏障实质上就是一个明显干涉声波传播的阻挡物或部分阻挡物，它可以阻挡声波的传播而形成一个声影区，声影区越大降噪效果越好，而且声波的绕射必然造成衰减。此外，声屏障还会对声波产生透射、反射现象。声屏障声影区的大小与噪声的频率有关（图</span><span>1</span><span style="font-family:宋体;">），频率越高，则声影区的范围越大；频率越低，则声影区的范围也越小。</span><span></span> \r\n</p>\r\n<p align="center" style="text-align:center;">\r\n	<span></span> \r\n</p>\r\n<br />\r\n<p style="text-indent:21.0pt;">\r\n	<span style="font-family:宋体;">从已建成的铁路声屏障研究及实际测量资料分析，一般传统直立式声屏障的降噪量为</span><span>4~6 dB(A)</span><span style="font-family:宋体;">。特别是当沿线存在高层建筑时，传统的声屏障对高层几乎没有什么降噪效果。研究表明，全封闭式及半封闭式声屏障是解决这一问题的有效途径。在利用全封闭式及半封闭式声屏障降低高速铁路噪声方面，沪杭高铁金华段采用了半封闭式声屏障设计（图</span><span>2</span><span style="font-family:宋体;">），以满足降噪量</span><span>12.5~13.1 dB(A)</span><span style="font-family:宋体;">的声学设计目标。</span><span></span> \r\n</p>\r\n<p align="center" style="margin-left:0cm;text-align:center;">\r\n	<br />\r\n</p>\r\n<p style="text-indent:21.0pt;">\r\n	<span style="font-family:宋体;">“小鸟天堂”是</span><span>390</span><span style="font-family:宋体;">多年前一棵独木成林的榕树，覆盖水面</span><span>1</span><span style="font-family:宋体;">万多</span><span>m<sup>2</sup></span><span style="font-family:宋体;">，形成全世界最大的独木古榕。树上鸟类众多，鸟树相依，人鸟相处，和谐奇特，是世间罕有的一道自然美丽风景线。新建深茂铁路新会段距离“小鸟天堂”较近的高架桥路段拟采用全封闭声屏障，以尽可能减小高铁噪声对鸟类的影响。拟选用的声屏障分两种形式，第一种是钢结构，立柱间距</span><span>2 m</span><span style="font-family:宋体;">，中间插入声屏障板材；第二种是混凝土结构。声屏障轮廓尺寸见图</span><span>3</span><span style="font-family:宋体;">。</span><span></span> \r\n</p>\r\n<p align="center" style="margin-left:0cm;text-align:center;">\r\n	<br />\r\n</p>\r\n<p style="text-indent:21.0pt;">\r\n	<span style="font-family:宋体;">本</span><span>SRTP</span><span style="font-family:宋体;">项目（省创）针对两种形式全封闭声屏障，利用</span><span>1:1</span><span style="font-family:宋体;">桥梁结构试验模型，研究吸声、隔声材料的选型及安装方式，采用人造声源模拟轮轨噪声，以测试两种全封闭声屏障增设前后的降噪效果，并将试验结果用于声屏障的声学选型。</span><span></span> \r\n</p>\r\n<p style="text-indent:21.0pt;">\r\n	<span style="font-family:宋体;"><br />\r\n</span> \r\n</p>\r\n<p style="text-indent:21.0pt;">\r\n	<span style="font-family:宋体;"> </span>\r\n</p>\r\n<p style="margin-left:0cm;text-indent:21.1pt;">\r\n	<b><span style="font-family:宋体;">指导教师简介：</span></b><b><span></span></b> \r\n</p>\r\n<p style="margin-left:0cm;text-indent:21.0pt;">\r\n	<span style="font-family:宋体;">张迅，男，博士，讲师，硕士生导师，西南交通大学首届“雏鹰学者”入选者。主要从事桥梁结构减振降噪研究。主持国家自然科学基金项目</span><span>1</span><span style="font-family:宋体;">项、四川省科技厅和教育厅科研项目各</span><span>1</span><span style="font-family:宋体;">项。主研国家自然基金项目</span><span>4</span><span style="font-family:宋体;">项（其中重点项目</span><span>1</span><span style="font-family:宋体;">项）、国家“</span><span>863</span><span style="font-family:宋体;">”计划项目</span><span>1</span><span style="font-family:宋体;">项。以第一</span><span>/</span><span style="font-family:宋体;">通讯作者发表</span><span>SCI</span><span style="font-family:宋体;">收录期刊论文</span><span>7</span><span style="font-family:宋体;">篇、</span><span>EI</span><span style="font-family:宋体;">收录期刊论文</span><span>14</span><span style="font-family:宋体;">篇。</span><span style="font-family:宋体;">联系邮箱：</span><span>zhxunxun@swjtu.edu.cn</span><span style="font-family:宋体;">。联系电话：</span><span>15902820405</span><span style="font-family:宋体;">。</span><span></span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>\r\n		  ', '张迅', '2016-03-13 18:16:44', NULL),
(51, '65ABB86399DC98B4', '西部交通企业低碳战略及其竞争力模拟研究', '全校\r', '\r\n		    需要计算机能力,联系电话：13018235204\r\n		  ', '范莉莉', '2016-03-13 13:38:21', NULL),
(52, 'D7BD303FBF7714CF', '企业碳资产识别及其关系研究', '全校\r', '\r\n		    企业碳资产如何界定、其之间的相互关系怎样？联系电话：13018235204\r\n		  ', '范莉莉', '2016-03-13 13:34:20', NULL),
(53, '920E579A12D1A11D', '企业低碳发展动态演化仿真研究', '全校\r', '\r\n		    要求计算机能力联系电话：13018235204\r\n		  ', '范莉莉', '2016-03-13 13:28:41', NULL),
(54, 'AC5A6C3A52B23AF4', '企业科技创新模式选择研究', '全校\r', '\r\n		    创新模式各类及其如何选择研究，联系电话：13018235204\r\n		  ', '范莉莉', '2016-03-13 13:27:17', NULL),
(55, '30C2D63C8AFF3C4F', '企业科技创新评价研究', '全校\r', '\r\n		    评价指标体系及评价方法，联系电话：13018235204\r\n		  ', '范莉莉', '2016-03-13 13:26:15', NULL),
(56, '83FB086813C45F9D', '泡沫轻质混凝土抗渗性影响因素试验研究', '全校\r', '\r\n		    &nbsp;\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;font-size:12pt;">泡沫轻质混凝土是由水泥、水、集料和外加剂（可不用）按比例混合形成浆体，然后再与由发泡剂水溶液制成的细小泡沫混合均匀，在施工现场或工厂浇筑成型、养护而成的一种多孔材料。泡沫轻质混凝土固有的多孔性导致了其渗透性，虽然影响泡沫轻质混凝土耐久性的各种破坏过程(如冻融破坏、碳化破坏、侵蚀破坏等)的破坏机理不尽相同，但几乎都与泡沫轻质混凝土的透水性有着密切关系。因此，泡沫轻质混凝土的抗渗性是决定其耐久性的一个重要因素。但有关泡沫轻质混凝土的<strong>抗渗性方面</strong>的研究却很少，为了加深对抗渗性的认识和研究，有必要开展泡沫轻质混凝土抗渗性试验研究。<span></span></span> \r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;font-size:12pt;">具体研究内容：</span> \r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;font-size:12pt;"></span><span style="font-family:宋体;font-size:12pt;"><span>（1）</span></span><span style="font-family:宋体;font-size:12pt;">采用增大水泥用量，减小水灰比的方法，<strong>开展室内试验</strong>，研究水灰比对泡沫轻质混凝土抗渗性的影响。<span></span></span> \r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;font-size:12pt;">（<span>2</span>）采用加入矿物掺合料的方法，<strong>开展室内试验</strong>，研究矿物掺合料和纤维对泡沫轻质混凝土抗渗性的影响。</span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-family:SimSun;color:#000000;font-size:10pt;"></span>&nbsp;\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-family:SimSun;color:#000000;font-size:10pt;">诚邀有兴趣的同学参加我们的团队申请校</span><span style="font-family:SimSun;color:#000000;font-size:10pt;">SRTP</span><span style="font-family:SimSun;color:#000000;font-size:10pt;">、国创项目</span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-family:SimSun;color:#000000;font-size:10pt;">联系信息：土木学院</span><span style="font-size:10pt;"> </span><span style="font-family:SimSun;color:#000000;font-size:10pt;">苏谦</span><span style="font-size:10pt;"> </span>\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span style="color:#000000;">邮箱：</span><span><a href="mailto:suqian@126.com"><span style="font-family:Times New Roman;color:#000000;">suqian@126.com</span></a> </span></span>\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span><span style="font-family:SimSun;color:#000000;">电话：13688319706</span><span style="font-family:SimSun;color:#000000;">&nbsp;</span></span></span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span></span></span><span style="font-family:SimSun;color:#000000;font-size:10pt;">教师主页：</span><span style="font-family:SimSun;color:#000000;font-size:10pt;"><a href="http://userweb.swjtu.edu.cn/Userweb/tmsq/index.htm"><span style="color:#000000;">http://userweb.swjtu.edu.cn/Userweb/tmsq/index.htm</span></a></span> \r\n</p>\r\n<p style="text-indent:21pt;">\r\n	&nbsp;\r\n</p>\r\n		  ', '苏谦', '2016-03-12 23:40:22', NULL),
(57, '2632018F472DA8E1', '泡沫轻质混凝土干缩性优化试验研究', '全校\r', '\r\n		    &nbsp;\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;font-size:12pt;">泡沫轻质混凝土是通过发泡机的发泡系统将发泡剂用机械方式充分发泡，并将泡沫与水泥浆均匀混合，现浇施工或模具成型，经自然养护所形成的一种含有大量气孔的新型轻质土材料。在建设工程中其又被称为：轻质泡沫土、气泡混合轻质土、泡沫轻质土等。</span><span style="font-size:12pt;"></span> \r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;font-size:12pt;">泡沫轻质混凝土的收缩是指泡沫轻质混凝土成型后，在硬化和使用过程中由于水化、干湿变化、温度变化等原因引起的体积减小。干缩会造成泡沫轻质混凝土内部产生微观裂缝和原生裂缝，一旦施加作用荷载，再加以一定条件，这些裂缝就会发展成为肉眼可见的宏观裂缝，进而造成泡沫轻质混凝土的开裂，导致泡沫轻质混凝土性能劣化，耐久性降低。因此，有必要采取措施减小泡沫轻质混凝土的干缩，但目前有关泡沫轻质混凝土的<strong>干缩性方面的研究却很少</strong>，为了加深对泡沫轻质混凝土干缩性的认识和研究，有必要开展泡沫轻质混凝土干缩性试验研究。</span><span style="font-size:12pt;"></span> \r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;font-size:12pt;">具体研究内容：</span><span style="font-size:12pt;"></span> \r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;font-size:12pt;">通过<strong>室内试验</strong>的方法，对于不同密度等级的泡沫轻质混凝土，掺加矿物掺合料和纤维，研究矿物掺合料和纤维对泡沫轻质混凝土干缩性能的影响，最后提出一种减小泡沫轻质混凝土干缩的有效方法。</span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-family:SimSun;color:#000000;font-size:10pt;"></span>&nbsp;\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-family:SimSun;color:#000000;font-size:10pt;">诚邀有兴趣的同学参加我们的团队申请校</span><span style="font-family:SimSun;color:#000000;font-size:10pt;">SRTP</span><span style="font-family:SimSun;color:#000000;font-size:10pt;">、国创项目</span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-family:SimSun;color:#000000;font-size:10pt;">联系信息：土木学院</span><span style="font-size:10pt;"> </span><span style="font-family:SimSun;color:#000000;font-size:10pt;">苏谦</span><span style="font-size:10pt;"> </span>\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span style="color:#000000;">邮箱：</span><span><a href="mailto:suqian@126.com"><span style="font-family:Times New Roman;color:#000000;">suqian@126.com</span></a> </span></span>\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span><span style="font-family:SimSun;color:#000000;">电话：13688319706</span><span style="font-family:SimSun;color:#000000;">&nbsp;</span></span></span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span></span></span><span style="font-family:SimSun;color:#000000;font-size:10pt;">教师主页：</span><span style="font-family:SimSun;color:#000000;font-size:10pt;"><a href="http://userweb.swjtu.edu.cn/Userweb/tmsq/index.htm"><span style="color:#000000;">http://userweb.swjtu.edu.cn/Userweb/tmsq/index.htm</span></a></span> \r\n</p>\r\n<p style="text-indent:21pt;">\r\n	&nbsp;\r\n</p>\r\n		  ', '苏谦', '2016-03-12 23:39:15', NULL),
(58, '41CB61AE4EF8D527', '高速铁路无砟轨道路基基床翻浆病害整治材料改良试验研究', '全校\r', '\r\n		    <p style="text-indent:20pt;">\r\n	&nbsp;\r\n</p>\r\n<p style="text-indent:24.1pt;">\r\n	<span style="font-size:12pt;font-family:宋体;line-height:150%;">近年来，我国高速铁路运营里程不断增长，但随着时间的变化，在高速列车冲击荷载和自然营力作用下，无砟轨道和路基将不可避免地出现损伤和破坏，其中，无砟轨道路基基床翻浆是一种特殊的病害形式。目前，针对高速铁路无砟轨道路基基床翻浆病害，整治材料主要为聚氨酯高分子材料。其中聚氨酯的原料由硬质聚醚多元醇、软质聚醚多元醇和异氰酸酯等组份组成，<b>各组分的最优配合比仍有待研究</b>。该整治材料的抗压强度、抗折强度和抗冲击强度等<b>力学性质也需进一步测试</b>。由于目前针对高速铁路无砟轨道路基基床翻浆病害整治的研究较少，因此开展该项研究对高速铁路的发展具有重要意义。</span> \r\n</p>\r\n<p style="text-indent:24.1pt;">\r\n	<span style="font-size:12pt;font-family:宋体;line-height:150%;"><span><span style="font-size:12pt;font-family:宋体;">具体研究内容：<span></span></span> \r\n</p>\r\n<p style="margin-left:51pt;text-indent:-51pt;">\r\n	<span style="font-size:12pt;font-family:宋体;"><span>&nbsp;&nbsp; （1）</span></span><span style="font-size:12pt;font-family:宋体;">通过调研文献资料，了解高速铁路无砟轨道路基基床翻浆病害的整治材料和工艺，采用单因素实验法<b>开展室内试验</b>，研究聚氨酯高分子材料各组分的最优配合比。<span></span></span> \r\n</p>\r\n<p style="margin-left:51pt;text-indent:-51pt;">\r\n	<span style="font-size:12pt;font-family:宋体;"><span>&nbsp;&nbsp; （2）</span></span><b><span style="font-size:12pt;font-family:宋体;">通过室内模型试验及物理力学试验</span></b><span style="font-size:12pt;font-family:宋体;">，研究聚氨酯高分子材料的抗压强度、抗折强度和抗冲击强度等力学性质，同时开展用于无砟轨道路基基床翻浆病害整治的可行性研究。<span></span></span> \r\n</p>\r\n<p style="margin-left:51pt;text-indent:-51pt;">\r\n	&nbsp;\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;font-family:SimSun;color:#000000;">诚邀有兴趣的同学参加我们的团队申请校</span><span style="font-size:10pt;font-family:SimSun;color:#000000;">SRTP</span><span style="font-size:10pt;font-family:SimSun;color:#000000;">、国创项目</span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;font-family:SimSun;color:#000000;">联系信息：土木学院</span><span style="font-size:10pt;"> </span><span style="font-size:10pt;font-family:SimSun;color:#000000;">苏谦</span><span style="font-size:10pt;"> </span>\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span style="color:#000000;">邮箱：</span><span><a href="mailto:suqian@126.com"><span style="font-family:Times New Roman;color:#000000;">suqian@126.com</span></a> </span></span>\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span><span style="font-family:SimSun;color:#000000;">电话：13688319706</span><span style="font-family:SimSun;color:#000000;">&nbsp;</span></span></span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span></span></span><span style="font-size:10pt;font-family:SimSun;color:#000000;">教师主页：</span><span style="font-size:10pt;font-family:SimSun;color:#000000;"><a href="http://userweb.swjtu.edu.cn/Userweb/tmsq/index.htm"><span style="color:#000000;">http://userweb.swjtu.edu.cn/Userweb/tmsq/index.htm</span></a></span> \r\n</p>\r\n<p style="margin-left:51pt;text-indent:-51pt;">\r\n	&nbsp;\r\n</p>\r\n<p style="margin-left:51pt;text-indent:-51pt;">\r\n	&nbsp;\r\n</p>\r\n</span></span>\r\n		  ', '苏谦', '2016-03-12 23:31:23', NULL),
(59, 'E715229481D64BA4', '既有线有砟轨道基床翻浆整治新技术与材料研究', '全校\r', '\r\n		    &nbsp;\r\n<p style="text-indent:24pt;">\r\n	<span style="font-family:宋体;font-size:12pt;">铁路路基基床的翻浆冒泥病害是我国既有铁路线最<strong>常见</strong>的路基病害之一。传统既有线铁路建设施工时，由于对路基结构功能认识不足、设计标准低、工期紧等原因，基床填料的材质（粘粒含量高、透水性差、亲水性强等）、压实质量往往不能满足设计要求。运营多年以后，线路频繁出现道砟陷入基床、路基横向排水坡度破坏、道砟基床结构层混乱等病害现象，严重影响基础结构的排水功能，导致地表降水下渗至基床表层，不能及时排出。在列车动荷载的反复作用下，基床出现软化、泥化现象，泥化的土以泥浆的形态向道砟或通过道砟向外翻冒挤出，形成翻浆冒泥病害，进一步导致</span><span style="font-family:宋体;font-size:12pt;">路基顶面横行排水不畅，路基基床、道砟板结构，线路几何尺寸变化大，<strong>严重影响</strong>铁路运输安全</span><span style="font-family:宋体;font-size:12pt;">。</span><span style="font-size:12pt;"></span> \r\n</p>\r\n<p style="text-indent:24pt;">\r\n	<span style="font-family:宋体;font-size:12pt;">到目前为止，国内外学者对基床翻浆整治进行过许多研究，就发展趋势而言，<strong>化学方法</strong>最有前途，可从根本上改变土的工程性质。苏谦教授科研团队结合宝成线基床翻浆现场情况，研制出<strong>新型聚氨酯碎石复合结构层</strong>，用于整治基床翻浆病害。</span><span style="font-size:12pt;"></span> \r\n</p>\r\n<p style="text-indent:24pt;">\r\n	<span style="font-family:宋体;font-size:12pt;">本课题通过<strong>资料调研</strong>、<strong>理论学习</strong>、<strong>模型试验</strong>等方式进行有砟轨道基床翻浆整治新技术及材料的研究，针对铁路运营实际需求，进一步深化<strong>整治工艺</strong>研究；通过室内<strong>配方试验</strong>及<strong>物理力学试验</strong>，研究聚氨酯材料<strong>配合比</strong>及其与<strong>碎石粒径的最佳组合</strong>。</span><span style="font-size:12pt;"></span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-family:SimSun;color:#000000;font-size:10pt;">诚邀有兴趣的同学参加我们的团队申请校</span><span style="font-family:SimSun;color:#000000;font-size:10pt;">SRTP</span><span style="font-family:SimSun;color:#000000;font-size:10pt;">、国创项目</span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-family:SimSun;color:#000000;font-size:10pt;">联系信息：土木学院</span><span style="font-size:10pt;"> </span><span style="font-family:SimSun;color:#000000;font-size:10pt;">苏谦</span><span style="font-size:10pt;"> </span>\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span style="color:#000000;">邮箱：</span><span><a href="mailto:suqian@126.com"><span style="font-family:Times New Roman;color:#000000;">suqian@126.com</span></a> </span></span>\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span><span style="font-family:SimSun;color:#000000;">电话：13688319706</span><span style="font-family:SimSun;color:#000000;">&nbsp;</span></span></span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span></span></span><span style="font-family:SimSun;color:#000000;font-size:10pt;">教师主页：</span><span style="font-family:SimSun;color:#000000;font-size:10pt;"><a href="http://userweb.swjtu.edu.cn/Userweb/tmsq/index.htm"><span style="color:#000000;">http://userweb.swjtu.edu.cn/Userweb/tmsq/index.htm</span></a></span> \r\n</p>\r\n<p style="text-indent:24pt;">\r\n	&nbsp;\r\n</p>\r\n		  ', '苏谦', '2016-03-12 23:17:01', NULL),
(60, '611FD708445522BF', '土工离心机位移传感器支架改型研究', '全校\r', '\r\n		    &nbsp;\r\n<p style="text-indent:28pt;background:white;">\r\n	<span style="color:black;"><span style="font-size:16px;">离心模型试验能模拟原型自重应力水平，它已成为预测和验证岩土工程性状的不可替代的手段，在工程建设中具有特殊的作用。目前，土工离心模型试验技术作为一种最有效的物理模型试验方法，几乎涉及土木工程的所有领域，成为岩土工程技术研究中最先进、最主要的研究手段之一。土工离心模型试验是将土工模型置于高速旋转的离心机中，让模型承受大于重力加速度的离心加速度的作用，补偿因模型缩尺带来的土工构筑物自重的损失。在巨大离心力作用下，普通的位移计支架</span><strong><span style="font-family:宋体;font-size:16px;">自身的变形</span></strong><span style="font-size:16px;">很难得到控制，从而影响了位移测量。并且对于不同的实验情况，普通位移计支架兼容性较差，很难重复使用，造成人力物力的浪费。 </span><span></span></span>\r\n</p>\r\n<p style="text-indent:28pt;background:white;">\r\n	<span style="color:black;"><span style="font-size:16px;">针对上述问题，本课题采用</span><strong><span style="font-family:宋体;font-size:16px;">试验手段</span></strong><span style="font-size:16px;">，并通过</span><strong><span style="font-family:宋体;font-size:16px;">计算分析</span></strong><span style="font-size:16px;">，对传统的</span><strong><span style="font-family:宋体;font-size:16px;">位移计支架</span></strong><span style="font-size:16px;">进行改进。其目的是研制出一种可抵抗巨大离心力，且适用于土工离心机不同模型箱、不同实验情况的</span><strong><span style="font-family:宋体;font-size:16px;">新型位移传感器支架</span></strong><span style="font-size:16px;">，研究成果可申报</span><strong><span style="font-family:宋体;font-size:16px;">国家发明专利</span></strong><span style="font-size:16px;">和</span><strong><span style="font-family:宋体;font-size:16px;">实用新型专利</span></strong><span style="font-size:16px;">。 </span><span></span></span>\r\n</p>\r\n<span style="font-size:14px;">&nbsp;</span> \r\n<p style="text-indent:28pt;background:white;">\r\n	<span style="line-height:150%;font-family:SimSun;color:black;font-size:14pt;">&nbsp;\r\n</p>\r\n</span> \r\n<p style="text-indent:28pt;background:white;">\r\n	<span style="line-height:150%;color:black;font-size:14pt;"></span><SPAN style="FONT-FAMILY: ; FONT-SIZE: 14pt" Simsun?,?serif?;color:black;line-height:150%;?></span>&nbsp;\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-family:SimSun;color:#000000;font-size:10pt;">诚邀有兴趣的同学参加我们的团队申请校</span><span style="font-family:SimSun;color:#000000;font-size:10pt;">SRTP</span><span style="font-family:SimSun;color:#000000;font-size:10pt;">、国创项目</span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-family:SimSun;color:#000000;font-size:10pt;">联系信息：土木学院</span><span style="font-size:10pt;"> </span><span style="font-family:SimSun;color:#000000;font-size:10pt;">苏谦</span><span style="font-size:10pt;"> </span>\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span style="color:#000000;">邮箱：</span><span><a href="mailto:suqian@126.com"><span style="font-family:SimSun;color:#000000;">suqian@126.com</span></a> </span></span>\r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span><span style="font-family:SimSun;color:#000000;">电话：13688319706</span><span style="font-family:SimSun;color:#000000;">&nbsp;</span></span></span> \r\n</p>\r\n<p style="text-indent:20pt;">\r\n	<span style="font-size:10pt;"><span></span></span><span style="font-family:SimSun;color:#000000;font-size:10pt;">教师主页：</span><span style="font-family:SimSun;color:#000000;font-size:10pt;"><a href="http://userweb.swjtu.edu.cn/Userweb/tmsq/index.htm"><span style="color:#000000;">http://userweb.swjtu.edu.cn/Userweb/tmsq/index.htm</span></a></span> \r\n</p>\r\n<p style="text-indent:28pt;background:white;">\r\n	&nbsp;\r\n</p>\r\n		  ', '苏谦', '2016-03-12 22:55:26', NULL),
(61, '476622BD89490916', '几种山胡椒属植物中化学成分的活性筛选', '生命科学与工程学院\r', '\r\n		    <span lang="EN-US" style="font-family:;" roman",serif;font-size:12pt;mso-ansi-language:en-us;mso-fareast-language:zh-cn;mso-bidi-language:ar-sa;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;"="" new="" times="">\r\n<p>\r\n	<span new="" times="" roman";"="" roman";mso-hansi-font-family:"times="" roman";mso-bidi-font-size:10.5pt;mso-ascii-font-family:"times=""><span style="font-family:宋体;font-size:small;">拟对本研究组从乌药、绒毛山胡椒、四川山胡椒等几种山胡椒属植物中分离得到的化合物首次进行抑制脂肪酸合成酶（减肥和抑癌靶点）、抗肿瘤（人早幼粒白血病细胞</span></span><span lang="EN-US" style="font-family:;" new="" times="" roman",serif;mso-bidi-font-size:10.5pt;"=""><span style="font-size:small;">HL-60</span></span><span new="" times="" roman";"="" roman";mso-hansi-font-family:"times="" roman";mso-bidi-font-size:10.5pt;mso-ascii-font-family:"times=""><span style="font-family:宋体;font-size:small;">，人肝癌细胞</span></span><span lang="EN-US" style="font-family:;" new="" times="" roman",serif;mso-bidi-font-size:10.5pt;"=""><span style="font-size:small;">SMMC7721</span></span><span new="" times="" roman";"="" roman";mso-hansi-font-family:"times="" roman";mso-bidi-font-size:10.5pt;mso-ascii-font-family:"times=""><span style="font-family:宋体;font-size:small;">，人肺腺癌细胞</span></span><span lang="EN-US" style="font-family:;" new="" times="" roman",serif;mso-bidi-font-size:10.5pt;"=""><span style="font-size:small;">A549</span></span><span new="" times="" roman";"="" roman";mso-hansi-font-family:"times="" roman";mso-bidi-font-size:10.5pt;mso-ascii-font-family:"times=""><span style="font-family:宋体;font-size:small;">，人乳腺癌细胞</span></span><span lang="EN-US" style="font-family:;" new="" times="" roman",serif;mso-bidi-font-size:10.5pt;"=""><span style="font-size:small;">MCF-7</span></span><span new="" times="" roman";"="" roman";mso-hansi-font-family:"times="" roman";mso-bidi-font-size:10.5pt;mso-ascii-font-family:"times=""><span style="font-family:宋体;font-size:small;">，人结肠癌细胞</span></span><span lang="EN-US" style="font-family:;" new="" times="" roman",serif;mso-bidi-font-size:10.5pt;"=""><span style="font-size:small;">SW480</span></span><span new="" times="" roman";"="" roman";mso-hansi-font-family:"times="" roman";mso-bidi-font-size:10.5pt;mso-ascii-font-family:"times=""><span style="font-family:宋体;font-size:small;">、人乳腺癌细胞</span></span><span lang="EN-US" style="font-family:;" new="" times="" roman",serif;mso-bidi-font-size:10.5pt;"=""><span style="font-size:small;">MDA-MB-231</span></span><span new="" times="" roman";"="" roman";mso-hansi-font-family:"times="" roman";mso-bidi-font-size:10.5pt;mso-ascii-font-family:"times=""><span style="font-family:宋体;font-size:small;">等六种人源肿瘤细胞株）和神经系统保护活性筛选，本项目拟至少筛选</span></span><span lang="EN-US" style="font-family:;" new="" times="" roman",serif;mso-bidi-font-size:10.5pt;"=""><span style="font-size:small;">25-30</span></span><span new="" times="" roman";"="" roman";mso-hansi-font-family:"times="" roman";mso-bidi-font-size:10.5pt;mso-ascii-font-family:"times=""><span style="font-family:宋体;font-size:small;">个化合物的上述相关活性，预期得到较高活性</span></span><span lang="EN-US" style="font-family:;" new="" times="" roman",serif;mso-bidi-font-size:10.5pt;"=""><span style="font-size:small;">(</span></span><span lang="EN-US" style="font-family:;" new="" times="" roman",serif;"=""><span style="font-size:small;">IC</span><sub><span style="font-size:x-small;">50</span></sub><span style="font-size:small;"> </span></span><span style="color: black; font-size: 10.5pt;" new="" times="" roman";"=""><span style="font-family:宋体;">≤</span></span><span lang="EN-US" style="color: black; font-family:;" new="" times="" roman",serif;font-size:10.5pt;"="">20 </span><span lang="EN-US" style="font-family:;" new="" times="" roman",serif;mso-bidi-font-size:10.5pt;"=""><span style="font-size:small;">μM</span></span><span new="" times="" roman";"="" roman";mso-hansi-font-family:"times="" roman";mso-bidi-font-size:10.5pt;mso-ascii-font-family:"times=""><span style="font-family:宋体;font-size:small;">）的化合物</span></span><span lang="EN-US" style="font-family:;" new="" times="" roman",serif;mso-bidi-font-size:10.5pt;"=""><span style="font-size:small;">2-5</span></span><span new="" times="" roman";"="" roman";mso-hansi-font-family:"times="" roman";mso-bidi-font-size:10.5pt;mso-ascii-font-family:"times=""><span style="font-family:宋体;font-size:small;">个。本项目中化合物的多数活性筛选类型均为国内外首次开展，可以为该属植物中相关化合物类型的活性研究和药物开发提供参考。也可使参与的同学得到较好的科研训练！</span></span> \r\n</p>\r\n</span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n		  ', '蒋合众', '2016-03-12 22:04:40', NULL);
INSERT INTO `srtphire` (`id`, `oid`, `title`, `orienting`, `content`, `author`, `original_time`, `breviary`) VALUES
(62, '6BBC1FB807EDC9CB', '绒毛山胡椒叶化学成分初步研究', '生命科学与工程学院\r', '\r\n		    <span style="font:14px/21px 宋体;color:#000000;text-transform:none;text-indent:28px;letter-spacing:normal;word-spacing:0px;float:none;display:inline !important;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;background-color:#FFFFFF;-webkit-text-stroke-width:0px;"><span style="font:14px/normal 宋体;color:#000000;text-transform:none;text-indent:28px;letter-spacing:normal;word-spacing:0px;float:none;display:inline !important;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;background-color:#FFFFFF;-webkit-text-stroke-width:0px;"><span style="font:14px/21px 宋体;color:#000000;text-transform:none;text-indent:28px;letter-spacing:normal;word-spacing:0px;float:none;display:inline !important;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;background-color:#FFFFFF;-webkit-text-stroke-width:0px;"><span style="font-size:small;">\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="background:white;color:black;font-family:宋体;mso-bidi-font-family:Tahoma;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">本项目拟选用分布于四川峨眉山樟科山胡椒属植物——绒毛山胡椒作为研究对象，</span><span style="font-family:Calibri;">首次在国内外对其树叶中化学成分进行初步的分离纯化研究</span><span style="word-spacing:0px;float:none;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;-webkit-text-stroke-width:0px;"><span style="font-family:Calibri;">，<span style="word-spacing:0px;float:none;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;-webkit-text-stroke-width:0px;">通过天然药物化学研究方法以及波谱分析等手段鉴定其结构，预计得到</span><span lang="EN-US" style="background:white;color:black;mso-bidi-font-family:Tahoma;mso-bidi-font-size:10.5pt;">2-5</span></span><span style="background:white;color:black;font-family:宋体;mso-bidi-font-family:Tahoma;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">个化合物。为后续对其进行化学成分和药理活性的深入研究提供参考，同时<span style="word-spacing:0px;float:none;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;-webkit-text-stroke-width:0px;">使参与该项目的同学受到较为系统的天然药物化学研究方法和实验操作的训练。</span></span></span>\r\n</p>\r\n</span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<br />\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="word-spacing:0px;float:none;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;-webkit-text-stroke-width:0px;"><span style="background:white;color:black;font-family:宋体;mso-bidi-font-family:Tahoma;mso-bidi-font-size:10.5pt;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;"><span style="word-spacing:0px;float:none;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;-webkit-text-stroke-width:0px;"><br />\r\n</span></span></span>\r\n</p>\r\n<span style="font-size:small;"><br />\r\n<br />\r\n</span></span></span></span>\r\n		  ', '蒋合众', '2016-03-12 21:38:52', NULL),
(63, 'EC7B3E7A9BAAFE53', '四川山胡椒根中等极性部位化学成分初步研究', '生命科学与工程学院\r', '\r\n		    <span style="font-family: 宋体; font-size: 12pt;" new="" times="" roman";mso-font-kerning:1.0pt;mso-ansi-language:en-us;mso-fareast-language:zh-cn;mso-bidi-language:ar-sa;"="">\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:12pt;mso-bidi-font-family:Tahoma;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">&nbsp;&nbsp;&nbsp; 进行山胡椒属植物近五年的文献调研，包括药理药效学研究、提取分离方法、化学成分的及其生物活性等等方面的研究进展，重点是中小极性部位的化学成分及药理活性研究，并进行文献分析和归纳整理；在前期文献调研的基础上，根据实验室条件设计出四川山胡椒根中乙酸乙酯部位化学成分的分离方法，运用硅胶柱层析等多种方法进行四川山胡椒乙酸乙酯部位化学成分的初步提取分离，并运用现代分析测试方法进行化合物的结构鉴定，预计分离纯化到</span><span lang="EN-US" style="font-size:12pt;mso-bidi-font-family:Tahoma;"><span style="font-family:Calibri;">3-5</span></span><span style="font-family:宋体;font-size:12pt;mso-bidi-font-family:Tahoma;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">个化学成分，为后续进行系统研究提供参考，同时<span style="word-spacing:0px;float:none;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;-webkit-text-stroke-width:0px;">使参与该项目的同学受到较为系统的天然药物化学研究方法和实验操作的训练。</span></span>\r\n</p>\r\n<span style="font:14px/21px 宋体;color:#000000;text-transform:none;text-indent:28px;letter-spacing:normal;word-spacing:0px;float:none;display:inline !important;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;background-color:#FFFFFF;-webkit-text-stroke-width:0px;">\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-size:small;"><br />\r\n</span>\r\n</p>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:12pt;mso-bidi-font-family:Tahoma;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;"><span style="word-spacing:0px;float:none;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;-webkit-text-stroke-width:0px;"><br />\r\n</span></span>\r\n</p>\r\n<span style="font-size:small;"><br />\r\n<br />\r\n</span></span></span>\r\n		  ', '蒋合众', '2016-03-12 21:31:55', NULL),
(64, 'BC1A3544275F3498', '尖叶蓝花楹挥发油的成分及抗菌活性初步研究', '生命科学与工程学院\r', '\r\n		    <span style="font-family: 宋体; font-size: 12pt;" new="" times="" roman";mso-font-kerning:1.0pt;mso-ansi-language:en-us;mso-fareast-language:zh-cn;mso-bidi-language:ar-sa;"="">\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:12pt;mso-bidi-font-family:Tahoma;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">&nbsp;&nbsp;&nbsp; 本项目拟在国内外首次进行尖叶蓝花楹的挥发油成分及活性研究。要求参与的同学进行紫葳科蓝花楹属植物的挥发油的化学成分及药理活性研究文献调研，并进行文献分析和归纳整理；在前期文献调研的基础上，根据实验室条件设计出尖叶蓝花楹枝条挥发油化学成分的提取分离、分析检测和活性测试方法，预计得到一定量的挥发油，鉴定出</span><span lang="EN-US" style="font-size:12pt;mso-bidi-font-family:Tahoma;"><span style="font-family:Calibri;">10-20</span></span><span style="font-family:宋体;font-size:12pt;mso-bidi-font-family:Tahoma;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">个化学成分，并初步测试出抗菌等活性结果，为后续研究提供参考，同时<span style="word-spacing:0px;float:none;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;-webkit-text-stroke-width:0px;"><span style="background:white;color:black;">使参与该项目的同学受到较为系统的科研训练。</span></span></span>\r\n</p>\r\n<span style="font:14px/21px 宋体;color:#000000;text-transform:none;text-indent:28px;letter-spacing:normal;word-spacing:0px;float:none;display:inline !important;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;background-color:#FFFFFF;-webkit-text-stroke-width:0px;"><span style="font-size:small;"><br />\r\n</span>\r\n<p style="margin:0cm 0cm 0pt;">\r\n	<span style="font-family:宋体;font-size:12pt;mso-bidi-font-family:Tahoma;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;"><span style="word-spacing:0px;float:none;white-space:pre-wrap;font-size-adjust:none;font-stretch:normal;-webkit-text-stroke-width:0px;"><span style="background:white;color:black;"><br />\r\n</span></span></span>\r\n</p>\r\n<span style="font-size:small;"><br />\r\n<br />\r\n</span></span></span>\r\n		  ', '蒋合众', '2016-03-12 21:28:03', NULL),
(65, 'E1036D0D79F66BBE', '基于机器视觉的火焰探测器设计', '全校\r', '\r\n		    <p>\r\n	在消防机器人的身上，需要配备探测火源的火焰传感器，火焰传感器可以使用红外传感器，但由于红外传感器易受环境光线影响而出现误报。使用机器视觉的火焰探测器可以克服环境光线的影响，使探测精度和距离都得到提高。本设计使用Arduino单片机作为主控制器，将摄像头安装在小车上，对周边进行搜索，利用图像处理算法确定火源，当发现火焰时，用声音和闪光信号报警。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	要求有一定的数学理论基础和单片机能力。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	联系方式：QQ38688969\r\n</p>\r\n		  ', '徐图', '2016-03-12 18:19:27', NULL),
(66, '93B533E488FB601C', '基于PID算法的动态液位控制', '全校\r', '\r\n		    <p>\r\n	液位控制是工业中常见的过程控制，它对生产的影响不容忽视。单容器液位控制系统具有非线性，滞后，耦合等特征，能够很好的模拟工业过程特征。对于液位控制系统，采用PID控制器的比例，积分，微分系统，能更好的适应控制系统的参数变化和工作条件的变化。本课题利用西门子的S7-200PLC，采用STEP7梯形图编程，完成液位的PID控制，并用组态王进行组态控制。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	3~5人一队，要求会PLC编程，会用组态王编程组态。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	联系方式：QQ38688969\r\n</p>\r\n		  ', '徐图', '2016-03-12 18:17:46', NULL),
(67, '15E651A56CBBD473', '基于BS构架的铁路局劳模管理系统设计', '全校\r', '\r\n		    <p>\r\n	随着国家提出“互联网+”战略，许多信息都要通过互联网来发布，因此基于B/S结构的数据库应用，已经变得越来越广泛，淘宝网、支付宝，其实就是基于B/S结构的数据库应用程序的典范。本设计利用网络数据库开发功能，开发出基于B/S结构的铁路局劳模管理系统，各个站段能够利用Internet推荐基层劳模到铁路局工会，工会根据推荐理由确定最终的劳模人员，并能进行各种统计操作。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	要求会Java 开发平台Eclipse，或者.NET for C#，网络数据库Oracle或者MySQL，有程序员的潜质。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	联系方式：QQ38688969\r\n</p>\r\n		  ', '徐图', '2016-03-12 18:14:41', NULL),
(68, 'AE805C5D0733C338', '基于安卓的蓝牙示波器设计', '全校\r', '\r\n		    <p>\r\n	示波器是一种用途十分广泛的电子测量仪器。它能把肉眼看不见的电信号变换成看得见的图像，便于人们研究各种电现象的变化过程。常见的数字示波器价格比较昂贵，携带也不方便。本设计利用Arduino作为主控板，将输入信号通过A/D转换后，通过蓝牙模块，发送给手机，手机将接收的数据用图像进行显示，获得输入信号的波形。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	要求对硬件设计感兴趣，有一定动手能力。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	联系方式：QQ38688969\r\n</p>\r\n		  ', '徐图', '2016-03-12 18:10:43', NULL),
(69, '8E736B93B61A320C', '基于单片机的RFID门禁系统设计', '全校\r', '\r\n		    <p>\r\n	&nbsp;自动开锁的RFID门禁系统对于大家出行是非常方便的。本课题利用Arduino作为主控板，以CC1101无线模块做为通信介质，在“三通认证”的基础上，使阅读器和钥匙卡之间完成可靠的认证，实现自动开锁。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	3~5人一个队，要求有一定动手能力，会单片机编程。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	联系方式：QQ38688969\r\n</p>\r\n		  ', '徐图', '2016-03-12 18:08:48', NULL),
(70, 'CA6A12B246897EFF', '基于Arduino的磁悬浮控制', '全校\r', '\r\n		    <p>\r\n	磁悬浮是利用悬浮磁力使物体处于一个无摩擦、无接触悬浮的平衡状态技术，是建造磁悬浮列车的关键技术。为了弄清楚磁悬浮的工作原理和控制方式，设计了本课题。本设计利用Arduino作为主控板，通过2个L298N控制4个线圈产生磁场，控制浮子的姿态，由2个霍尔元件检测浮子的姿态，并返回给控制器，控制器通过PID算法调整线圈的控制电压，从而控制浮子的姿态。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	3~5人一个队，要求有一定动手能力，会单片机编程。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	联系方式：QQ38688969\r\n</p>\r\n		  ', '徐图', '2016-03-12 18:05:17', NULL),
(71, 'D1E6D9B3CF8B9859', '基于Arduino和LabVIEW的多路数据采集系统', '全校\r', '\r\n		    <p>\r\n	数据采集是自动控制系统必不可少的环节，为了获得被控对象的状态，就需要对传感器传来的数字量和模拟量进行数据采集。为了获得低速、低成本、快速搭建的多路数据采集系统，本设计利用Arduino作为下位机和A/D转换器，以LabView作为上位机程序，通过串口将上、下位机联系起来，完成4路模拟量、7路数字量、1路脉冲信号的采集，用按钮开关实现数字量输入，用电位器实现模拟信号输入。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	要求熟悉单片机编程，能熟悉使用labview。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	联系方式：QQ38688969\r\n</p>\r\n		  ', '徐图', '2016-03-12 18:02:52', NULL),
(72, 'D666A6C0373F9058', '基于LQR的柔性倒立摆控制', '全校\r', '\r\n		    <p>\r\n	倒立摆系统是一种非最小相位的强不稳定系统，它具有非线性、多变量和不稳定的特点，对它进行控制，会遇到很多自动控制中的经典问题：如鲁棒性问题、非线性问题、镇定问题、随动问题等，通过这些问题的解决，可以检验控制算法的性能，因此倒立摆是验证新型控制算法的理想平台。本课题利用LQR算法对直线柔性倒立摆进行倒立控制。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	3~5人一队，要求学过控制类课程，熟悉Matlab仿真编程。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	联系方式，QQ38688969\r\n</p>\r\n<p>\r\n	<u><span style="font-size:12pt;font-family:宋体;"></span></u>&nbsp;\r\n</p>\r\n		  ', '徐图', '2016-03-12 18:01:32', NULL),
(73, '76D58C1AE2803BE4', '面向车载图像的接触网支柱提取和故障检测', '全校\r', '\r\n		    <p>\r\n	系统输入：列车车头观测序列图像\r\n</p>\r\n<p>\r\n	系统输出：从图像中识别出以接触网支柱为主的关键设备，提取结构并分析状态\r\n</p>\r\n<p>\r\n	知识要求：擅长C/C++编程，了解数字图像处理，对机器学习有兴趣\r\n</p>\r\n<p>\r\n	联系： &nbsp;tang.peng@home.swjtu.edu.cn\r\n</p>\r\n		  ', '唐鹏', '2016-03-12 16:09:30', NULL),
(74, 'E9DC2E79D85B870B', '流域突发农药污染事故特征分析及应急处理技术研究', '全校\r', '\r\n		    <p>\r\n	<span style="text-indent:32px;white-space:normal;font-size:12pt;line-height:24px;font-family:宋体;"><span style="font-size:11pt;font-family:楷体;">1.研究背景：</span></span> \r\n</p>\r\n<p style="text-indent:0px;">\r\n	<span style="text-indent:32px;white-space:normal;line-height:24px;"><span style="font-family:楷体;font-size:11pt;">流域突发污染事故一般难以预料和预测，也难以避免，可视为一种必须与之共存的事件。对此，各级政府和各行业主管部门都根据相关标准规范制定了相应的突发水环境污染应急预案，但是其应用尚处于初级阶段，预案主要集中在应急组织管理指挥系统、应急工程救援保障体系、保障供应体系和应急队伍建设等方面。在如何应对流域突发污染事故处理方面，只有原则性的要求，没有具体的规定；导致污染应急预案呈现出一种逐步规范化，具有普遍适用性的特征，甚至被认为放之四海而皆准。然</span>而</span><span style="text-indent:24pt;font-size:11pt;font-family:楷体;">流域突发污染事故具有不同的地域性、不同的自然环境性、不同的产生原因，其污染影响特征也各异；即使</span><span style="text-indent:24pt;font-size:11pt;font-family:楷体;">相同污染物的不同污染事故，因事故原因不同，其事故的污染特征也不同，处理方案也就迥异。</span> \r\n</p>\r\n<p>\r\n	<span style="text-indent:24pt;white-space:normal;font-family:宋体;font-size:12pt;">2.研究内容</span> \r\n</p>\r\n<p>\r\n	<span style="text-indent:24pt;white-space:normal;font-family:宋体;font-size:12pt;">本项目通过运用事故树分析法<span style="font-size:11.0pt;font-family:楷体;">从“事故结果”导出“事故原因”，</span>对农药</span><span style="text-indent:24pt;white-space:normal;font-family:宋体;font-size:12pt;">引发的</span><span style="text-indent:24pt;white-space:normal;font-family:宋体;font-size:12pt;">流域污染事故进行特征</span><span style="text-indent:24pt;white-space:normal;font-family:宋体;font-size:12pt;">分析，在此基础上</span><span style="text-indent:24pt;white-space:normal;font-family:宋体;font-size:12pt;">提出相应的应急处理技术，为流域突发性污染事故的应急处理提供理论依据。</span> \r\n</p>\r\n<p>\r\n	<span style="text-indent:24pt;white-space:normal;font-family:宋体;font-size:12pt;">3.任务要求</span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:medium;text-indent:32px;">研究成果发表论文一篇</span> \r\n</p>\r\n<p>\r\n	<span style="text-indent:24pt;white-space:normal;font-family:宋体;font-size:12pt;">4.联系方式</span> \r\n</p>\r\n<p>\r\n	<span style="text-indent:24pt;white-space:normal;font-family:宋体;font-size:12pt;">邮箱642823770@qq.com，qq：642823770</span> \r\n</p>\r\n		  ', '刘颖', '2016-03-12 16:05:13', NULL),
(75, 'AEB03B45793859CA', '流域突发重金属污染事故特征分析及应急处理技术研究', '全校\r', '\r\n		    <span style="text-indent:32px;white-space:normal;line-height:24px;"><span style="text-indent:32px;white-space:normal;"> \r\n<p style="font-family:宋体;font-size:16px;white-space:normal;">\r\n	<span style="text-indent:32px;font-size:12pt;line-height:24px;font-family:宋体;"><span style="font-size:11pt;font-family:楷体;">1.研究背景：</span></span> \r\n</p>\r\n<p style="white-space:normal;">\r\n	<span style="text-indent:32px;line-height:24px;"><span style="font-family:楷体;font-size:11pt;">流域突发污染事故一般难以预料和预测，也难以避免，可视为一种必须与之共存的事件。对此，各级政府和各行业主管部门都根据相关标准规范制定了相应的突发水环境污染应急预案，但是其应用尚处于初级阶段，预案主要集中在应急组织管理指挥系统、应急工程救援保障体系、保障供应体系和应急队伍建设等方面。在如何应对流域突发污染事故处理方面，只有原则性的要求，没有具体的规定；导致污染应急预案呈现出一种逐步规范化，具有普遍适用性的特征，甚至被认为放之四海而皆准。</span><span style="font-family:宋体;font-size:small;">然而</span></span><span style="font-family:楷体;font-size:11pt;text-indent:24pt;">流域突发污染事故具有不同的地域性、不同的自然环境性、不同的产生原因，其污染影响特征也各异；即使</span><span style="font-family:楷体;font-size:11pt;text-indent:24pt;">相同污染物的不同污染事故，因事故原因不同，其事故的污染特征也不同，处理方案也就迥异。</span> \r\n</p>\r\n<p style="font-family:宋体;font-size:16px;white-space:normal;">\r\n	<span style="text-indent:24pt;font-family:宋体;font-size:12pt;">2.研究内容</span> \r\n</p>\r\n<p style="font-family:宋体;font-size:16px;white-space:normal;">\r\n	<span style="text-indent:24pt;font-family:宋体;font-size:12pt;">本项目通过运用事故树分析法<span style="font-size:11pt;font-family:楷体;">从“事故结果”导出“事故原因”，</span>对重金属</span><span style="text-indent:24pt;font-family:宋体;font-size:12pt;">引发的</span><span style="text-indent:24pt;font-family:宋体;font-size:12pt;">流域污染事故进行特征</span><span style="text-indent:24pt;font-family:宋体;font-size:12pt;">分析，在此基础上</span><span style="text-indent:24pt;font-family:宋体;font-size:12pt;">提出相应的应急处理技术，为流域突发性污染事故的应急处理提供理论依据。</span> \r\n</p>\r\n<p style="font-family:宋体;font-size:16px;white-space:normal;">\r\n	<span style="text-indent:24pt;font-family:宋体;font-size:12pt;">3.任务要求</span> \r\n</p>\r\n<p style="font-family:宋体;font-size:16px;white-space:normal;">\r\n	<span style="font-family:宋体;font-size:medium;text-indent:32px;">研究成果发表论文一篇</span> \r\n</p>\r\n<p style="font-family:宋体;font-size:16px;white-space:normal;">\r\n	<span style="text-indent:24pt;font-family:宋体;font-size:12pt;">4.联系方式</span> \r\n</p>\r\n<p style="font-family:宋体;font-size:16px;white-space:normal;">\r\n	<span style="text-indent:24pt;font-family:宋体;font-size:12pt;">邮箱642823770@qq.com，qq：642823770</span> \r\n</p>\r\n</span></span><span style="text-indent:24pt;white-space:normal;font-family:宋体;font-size:12pt;"></span>\r\n		  ', '刘颖', '2016-03-12 16:04:24', NULL),
(76, '2BE5D9A0AC8E0114', '流域突发苯类污染事故特征分析及应急处理技术研究', '全校\r', '\r\n		    <p style="text-indent:24.0pt;">\r\n	<span style="font-size:12.0pt;line-height:150%;font-family:宋体;"><span style="font-family:宋体;font-size:16px;text-indent:32px;white-space:normal;"> </span></span> \r\n</p>\r\n<p style="white-space:normal;">\r\n	<span style="text-indent:32px;font-size:12pt;line-height:24px;font-family:宋体;"><span style="font-size:11pt;font-family:楷体;">1.研究背景：</span></span> \r\n</p>\r\n<p style="text-indent:0px;white-space:normal;">\r\n	<span style="text-indent:32px;line-height:24px;"><span style="font-family:楷体;font-size:11pt;">流域突发污染事故一般难以预料和预测，也难以避免，可视为一种必须与之共存的事件。对此，各级政府和各行业主管部门都根据相关标准规范制定了相应的突发水环境污染应急预案，但是其应用尚处于初级阶段，预案主要集中在应急组织管理指挥系统、应急工程救援保障体系、保障供应体系和应急队伍建设等方面。在如何应对流域突发污染事故处理方面，只有原则性的要求，没有具体的规定；导致污染应急预案呈现出一种逐步规范化，具有普遍适用性的特征，甚至被认为放之四海而皆准。</span>然而</span><span style="text-indent:24pt;font-size:11pt;font-family:楷体;">流域突发污染事故具有不同的地域性、不同的自然环境性、不同的产生原因，其污染影响特征也各异；即使</span><span style="text-indent:24pt;font-size:11pt;font-family:楷体;">相同污染物的不同污染事故，因事故原因不同，其事故的污染特征也不同，处理方案也就迥异。</span> \r\n</p>\r\n<p style="white-space:normal;">\r\n	<span style="text-indent:24pt;font-family:宋体;font-size:12pt;">2.研究内容</span> \r\n</p>\r\n<p style="white-space:normal;">\r\n	<span style="text-indent:24pt;font-family:宋体;font-size:12pt;">本项目通过运用事故树分析法<span style="font-size:11pt;font-family:楷体;">从“事故结果”导出“事故原因”，</span>对苯系物</span><span style="text-indent:24pt;font-family:宋体;font-size:12pt;">引发的</span><span style="text-indent:24pt;font-family:宋体;font-size:12pt;">流域污染事故进行特征</span><span style="text-indent:24pt;font-family:宋体;font-size:12pt;">分析，在此基础上</span><span style="text-indent:24pt;font-family:宋体;font-size:12pt;">提出相应的应急处理技术，为流域突发性污染事故的应急处理提供理论依据。</span> \r\n</p>\r\n<p style="white-space:normal;">\r\n	<span style="text-indent:24pt;font-family:宋体;font-size:12pt;">3.任务要求</span> \r\n</p>\r\n<p style="white-space:normal;">\r\n	<span style="font-family:宋体;font-size:medium;text-indent:32px;">研究成果发表论文一篇</span> \r\n</p>\r\n<p style="white-space:normal;">\r\n	<span style="text-indent:24pt;font-family:宋体;font-size:12pt;">4.联系方式</span> \r\n</p>\r\n<p style="white-space:normal;">\r\n	<span style="text-indent:24pt;font-family:宋体;font-size:12pt;">邮箱642823770@qq.com，qq：642823770</span> \r\n</p>\r\n<span style="font-family:宋体;font-size:12pt;text-indent:24pt;"></span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p style="text-indent:24.0pt;">\r\n	<span style="font-size:12.0pt;line-height:150%;"></span> \r\n</p>\r\n		  ', '刘颖', '2016-03-12 16:03:15', NULL),
(77, '3159F838C49EAC1F', '求解多目标二次分配问题的优化算法研究', '全校\r', '\r\n		    <p>\r\n	<span style="white-space:nowrap;">项目背景：二次分配问题（Quadratic Assignment Problem，QAP）是一个经典的组合优化问题，广泛应用于工厂布局、作业车间<span style="white-space:nowrap;">调度、</span><span style="line-height:1.5;white-space:nowrap;">挡板布线等实际问题，有效的解决</span><span style="line-height:1.5;white-space:nowrap;">大规模二次分配问题，具有非常重要的应用价值。</span></span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">任务要求：</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">（1）了解NP-难问题；</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">（2）熟练掌握C/C++编程语言；</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">（3）熟悉数据结构和算法设计；</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">（4）以论文形式完成本项目的研究。</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">选题人数：1～2人（专业不限）</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">联系方式：zrq@home.swjtu.edu.cn</span> \r\n</p>\r\n		  ', '曾荣强', '2016-03-12 11:53:03', NULL),
(78, '54C66C24B8D2A25F', '求解多目标无约束二元二次规划问题的优化算法研究', '全校\r', '\r\n		    <p>\r\n	<span style="white-space:nowrap;">项目背景：无约束二元二次规划问题（Unconstrained Binary Quadratic Programming Problem，UBQP）是一个具有重<span style="white-space:nowrap;">要理论<span style="line-height:1.5;white-space:nowrap;">意义的组合优化问题，也是一个NP-难问题，广泛应用于计算机辅助设计、交通管理、手机通信频率分配等实际问</span><span style="line-height:1.5;white-space:nowrap;">题。传统的<span style="line-height:1.5;white-space:nowrap;">凸规划方法难以有效的解决大规模UBQP问题，因而，元启发式方法是一种非常有效的解决大规模组合优化问</span><span style="line-height:1.5;white-space:nowrap;">题的工具。</span></span></span></span>\r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;"></span>\r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;">任务要求：</span> \r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;">（1）了解NP-难问题；</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">（2）熟练掌握C/C++编程语言；</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">（3）熟悉数据结构和算法设计；</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">（4）以论文形式完成本项目的研究。</span> \r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;">选题人数：1～2人（专业不限）</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">联系方式：zrq@home.swjtu.edu.cn</span> \r\n</p>\r\n		  ', '曾荣强', '2016-03-12 11:48:49', NULL),
(79, '46A9D0EACC298049', ' 求解多目标最大割问题的优化算法研究', '全校\r', '\r\n		    <p>\r\n	<span style="white-space:nowrap;">项目背景：最大割问题（Max-Cut Problem）是一个经典的组合优化问题，也是一个NP-难问题，广泛应用于电路布局设<span style="white-space:nowrap;">计、统计<span style="white-space:nowrap;">物理、无线通讯等实际问题。传统的整数规划方法难以有效的解决大规模最大割问题，因而，元启发式方法是<span style="line-height:1.5;white-space:nowrap;">一种非常有效的解<span style="line-height:1.5;white-space:nowrap;">决大规模组合优化问题的工具。</span></span></span></span></span>&nbsp;<span style="white-space:nowrap;"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;">任务要求：</span> \r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;">（1）了解NP-难问题；</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">（2）熟练掌握C/C++编程语言；</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">（3）熟悉数据结构和算法设计；</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">（4）以论文形式完成本项目的研究。</span> \r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;">选题人数：1～2人（专业不限）</span> \r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">联系方式：zrq@home.swjtu.edu.cn</span> \r\n</p>\r\n		  ', '曾荣强', '2016-03-12 11:45:37', NULL),
(80, '32E911A663E1AC9A', '我校新闻传播学科微信公众号建设', '艺术与传播学院\r', '\r\n		    目前我校新闻传播学科还没有建立微信公众号，希望课题组与指导老师讨论，在比较综合其他相似账号基础上，确定名称与基本定位，申请建立公众号，并在项目期内成功运营。具体需与指导教师讨论。\r\n		  ', '蒋宁平', '2016-03-12 07:19:59', NULL),
(81, '5C265C2F4AE0A784', '传统媒体的移动化与社交化：以移动客户端为例', '艺术与传播学院\r', '\r\n		    传统媒体面对新媒体的冲击，纷纷作出了融合发展的努力。其中建立自己的移动客户端成为最重要的选择。希望课题组选择有代表性的媒体其及客户端，对其定位、功能、界面、推广、运营、效果等进行全方位研究，并提出相应的传统媒体建设客户端的建议。\r\n		  ', '蒋宁平', '2016-03-12 07:14:43', NULL),
(82, '475F4EA7D6F6F504', '政务微博研究', '艺术与传播学院\r', '\r\n		    <p>\r\n	选择国内有代表性的政务微博，设计评估指标，研究政务微博运营中存在的问题，并提出相应的建议。具体思路与指导老师讨论确定。\r\n</p>\r\n		  ', '蒋宁平', '2016-03-12 07:08:23', NULL),
(83, '424A2EC8DB58C058', '城市对外传播研究', '艺术与传播学院\r', '\r\n		    随着我国对外合作与交流的增多，许多城市开展了对外传播，如开设对外英文网站，在境外社交媒体设立账号并运营。期待项目组选择几个有代表性的城市进行研究，评估其水平，总结特点与不足，提出相对的对策建议。具体与指导老师讨论确定。\r\n		  ', '蒋宁平', '2016-03-12 07:03:35', NULL),
(84, '375517AFDF1DA960', '纳米镁基复合材料制备与塑形变形研究', '全校\r', '\r\n		    <p>\r\n	纳米碳化硅、石墨烯、碳纳米管等增强镁基复合材料具有低密度、高强度、大韧性和高导电性，是典型的结构-功能一体化材料。\r\n</p>\r\n<p>\r\n	招募勇于创新、敢于尝试、善于团队合作的本科同学，一起探索这个有趣而未知的世界！\r\n</p>\r\n<p>\r\n	要求：小组不超过5人。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	导师：权高峰，博士、教授、博士生导师、德国归国华侨，轨道交通国家实验室交通运输装备轻量化研究所所长、四川省“千人计划”特聘专家\r\n</p>\r\n		  ', '权高峰', '2016-03-12 00:27:10', NULL),
(85, '36514356896561BE', '德国与欧洲难民危机', '全校\r', '\r\n		    <p style="margin-left:2.5000pt;text-align:left;">\r\n	<span style="font-family:宋体;font-size:12.0000pt;">自2015<span>年4</span><span>月欧洲难民危机爆发以来，德国在难民危机中所表现出的立场经历了从犹豫不决、到表示欢迎再到重新收紧避难政策</span></span><span style="font-family:宋体;font-size:12.0000pt;">三个阶段</span><span style="font-family:宋体;font-size:12.0000pt;">。本项目立足于欧洲难民危机中的</span><span style="font-family:宋体;font-size:12.0000pt;">德国，探讨</span><span style="font-family:宋体;font-size:12.0000pt;">难民危机给德国社会带来的后果，德国应对难民危机所采取的诸多政策以及难民危机对欧盟内部的影响等。</span><span style="font-family:宋体;font-size:12.0000pt;"></span> \r\n</p>\r\n<p style="margin-left:2.5000pt;text-align:left;">\r\n	<span style="font-family:宋体;font-size:12.0000pt;">对本项目感兴趣的同学，请联系：tangximin@126.com</span><span style="font-family:宋体;font-size:12.0000pt;"></span> \r\n</p>\r\n		  ', '汤习敏', '2016-03-11 23:54:24', NULL),
(86, '0E1F23C5A9259989', '德国与欧洲一体化进程', '全校\r', '\r\n		    <p>\r\n	<span style="font-family:宋体;font-size:12.0000pt;">德国</span><span style="font-family:宋体;font-size:12.0000pt;">是</span><span style="font-family:宋体;font-size:12.0000pt;">欧盟中最强大的经济实体</span><span style="font-family:宋体;font-size:12.0000pt;">，</span><span style="font-family:宋体;font-size:12.0000pt;">德法两国</span><span style="font-family:宋体;font-size:12.0000pt;">被称为启动</span><span style="font-family:宋体;font-size:12.0000pt;">欧洲</span><span style="font-family:宋体;font-size:12.0000pt;">一体化前进的“发动机”，德英法三国在</span><span style="font-family:宋体;font-size:12.0000pt;">欧洲</span><span style="font-family:宋体;font-size:12.0000pt;">一体化进程中也被称为一体化的“三驾马车”</span><span style="font-family:宋体;font-size:12.0000pt;">。</span><span style="font-family:宋体;font-size:12.0000pt;">本项目旨在考查德国在</span><span style="font-family:宋体;font-size:12.0000pt;">推进欧洲一体化的进程中起着</span><span style="font-family:宋体;font-size:12.0000pt;">怎样</span><span style="font-family:宋体;font-size:12.0000pt;">举足轻重的作用</span><span style="font-family:宋体;font-size:12.0000pt;">，</span><span style="font-family:宋体;font-size:12.0000pt;">由此对理解当代德国在欧盟框架内做出的政策提供一个历史的背景。</span><span style="font-family:宋体;font-weight:bold;font-size:12.0000pt;"></span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-weight:bold;font-size:12.0000pt;">本项目要求学生对</span><span style="font-family:宋体;font-weight:bold;font-size:12.0000pt;">德国现代史、德法关系史、欧盟发展史</span><span style="font-family:宋体;font-weight:bold;font-size:12.0000pt;">有一定的了解，通过调查研究做出自己的分析判断</span><span style="font-family:宋体;font-weight:bold;font-size:12.0000pt;">，</span><span style="font-family:宋体;font-weight:bold;font-size:12.0000pt;">最后以论文形式完成本项目的研究，培养自身的创新研究能力</span><span style="font-family:宋体;font-weight:bold;font-size:12.0000pt;">。</span> \r\n</p>\r\n<p>\r\n	<p>\r\n		<span style="font-family:宋体;font-size:10.5000pt;">邮箱：tangximin@126.com</span><span style="font-family:宋体;font-size:10.5000pt;"></span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n		  ', '汤习敏', '2016-03-11 23:29:12', NULL),
(87, '6CF8CD56AB611271', '基于多摄像头视频的目标追踪系统设计', '全校\r', '\r\n		    ①掌握目前主流的视频监控技术及视频分析技术原理及应用；<br />\r\n②查阅10000字符以上的相关外文资料；<br />\r\n③掌握系统设计和针对特定硬件的软件开发技术；<br />\r\n④实现基于多摄像头视频的目标追踪系统；<br />\r\n⑤撰写科创设计论文，要求10000字以上。\r\n		  ', '李德智', '2016-03-11 22:42:20', NULL),
(88, 'FB51FC2A3647BC31', '企业低碳竞争力影响因素分析', '全校\r', '\r\n		    <p>\r\n	分析有哪些主要影响因素，如何影响企业低碳竞争力\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	电话，13018235204\r\n</p>\r\n		  ', '范莉莉', '2016-03-11 22:41:43', NULL),
(89, '2FE2EE575A7959CF', '基于DLP9000X芯片组的3D打印应用设计', '全校\r', '\r\n		    ①掌握目前主流的3D打印技术原理及应用；<br />\r\n②查阅10000字符以上的3D打印相关外文资料；<br />\r\n③完成3D打印机的结构设计以及温度控制、方向控制、喷头移动及喷出量调节的设计；<br />\r\n④实现简单物体的3D打印全流程仿真软件；<br />\r\n⑤撰写科创设计论文，要求10000字以上。\r\n		  ', '李德智', '2016-03-11 22:40:35', NULL),
(90, 'B4592B04A6FE1E50', '高烈度山区曲线高墩大跨桥梁的地震响应研究', '土木工程学院\r', '\r\n		    <p>\r\n	高烈度山区桥梁往往受到地形、地质和地貌的限制，结构体系和结构模式较平原地区桥梁复杂，此类桥梁的地震响应是一个值得深入研究的课题。本课题拟通过结合某高烈度山区大跨曲线高墩桥梁开展地震响应研究，初步使本科生同学对桥梁结构的地震响应分析有初步的认识和了解。具体参数如下：\r\n</p>\r\n<p>\r\n	1）、工程背景：某具体山区高墩曲线大跨桥梁工程。\r\n</p>\r\n<p>\r\n	2）、分析类型：响应谱分析、时程分析。\r\n</p>\r\n<p>\r\n	3）、地震波：人工波、场地记录波。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n		  ', '占玉林', '2016-03-11 21:24:28', NULL),
(91, 'FFDEFF6239F7BE95', '考虑弹塑性影响的钢-混凝土组合梁有效分布宽度研究', '土木工程学院\r', '\r\n		    <p>\r\n	本项目旨在针对目前国内外规范中对钢-混凝土组合梁的有效分布宽度仅考虑弹性影响的不足，开展考虑弹塑性影响的钢-混凝土组合梁有效分布宽度的研究。主要研究的目标为通过研究弹塑性影响下的钢-混凝土组合梁的有效分布宽度，得到更合理、更经济的设计指标。研究设计的主要参数<span style="line-height:1.5;">如下：</span>\r\n</p>\r\n<p>\r\n	1）、结构体系：简支梁体系的钢-混凝土组合梁。\r\n</p>\r\n<p>\r\n	2）、荷载模式：跨中集中力和均布荷载。\r\n</p>\r\n<p>\r\n	3）、本构模型：a）、混凝土材料本构采用经典混凝土非线性本构；b）钢材：理想弹塑性本构模型。\r\n</p>\r\n<p>\r\n	4）、界面：完全粘结。\r\n</p>\r\n<p>\r\n	5）、软件要求：ANSYS大型空间分析有限元软件。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n		  ', '占玉林', '2016-03-11 21:08:46', NULL),
(92, '392430BC4FA02D34', '环境因素影响下轨道板抗氯离子渗透特性试验研究', '土木工程学院\r', '\r\n		    <p>\r\n	<span><span style="font-size:14px;font-family:SimSun;">&nbsp;&nbsp;&nbsp;&nbsp;无砟轨道结构作为列车运行的引导结构，在承受列车荷载的同时受到环境因素的影响，如温度作用、离子侵蚀等。</span><span style="font-family:宋体;line-height:150%;"><span style="font-size:14px;font-family:SimSun;">混凝土中<span style="font-family:宋体;line-height:150%;"><span style="font-size:14px;font-family:SimSun;">氯离子</span></span>的渗透问题是一个受混凝土所处环境因素、内部组成以及材料特性等诸多因素影响的复杂过程，在此过程中不仅有物理变化，还有化学变化。</span><span><span style="font-family:Tahoma;"><span style="font-size:14px;font-family:SimSun;">轨道板</span><span style="font-size:14px;font-family:SimSun;">抗氯离子渗透性能是评估轨道结构耐久性能</span><span style="font-size:14px;font-family:SimSun;">的主要因素。</span></span></span></span><span style="font-size:14px;font-family:SimSun;">因此研究环境因素影响下</span><span style="font-size:14px;font-family:SimSun;">轨道板抗氯离子渗透特性的影响具有重要意义。</span></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"></span><span style="font-family:SimSun;">&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style="font-family:SimSun;"></span><span style="font-size:14px;font-family:SimSun;">本研究要求如下：</span> \r\n</p>\r\n<p align="left">\r\n	<span><span style="font-size:14px;font-family:NSimSun;"><span style="font-family:NSimSun;"><span style="font-size:14px;font-family:NSimSun;"><span style="font-size:14px;font-family:NSimSun;">&nbsp;&nbsp;&nbsp; 1</span></span></span></span><span style="font-size:14px;font-family:NSimSun;">、有较强动手能力，喜欢做实验，对材料科学感兴趣；</span><SPAN style="FONT-FAMILY: " Tahoma?,sans-serif;?> <span></span></span>\r\n</p>\r\n<p align="left">\r\n	<span style="font-size:14px;"><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2</span></span><span><span style="font-size:14px;">、广泛的资料调研，弄清混凝土中氯离子渗透主要机理及其主要影响因素</span><span style="font-size:14px;">；</span></span> \r\n</p>\r\n<p align="left">\r\n	<span style="font-size:14px;"><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3</span></span><span style="font-family:SimSun;"><span style="font-size:14px;">、分析温度作用对</span><span style="font-size:14px;">下</span><span style="font-size:14px;">轨道板抗氯离子渗透的影响；</span></span> \r\n</p>\r\n<p align="left">\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;4、分析酸雨环境对</span><span style="font-size:14px;">轨道板抗氯离子渗透的影响；</span> \r\n</p>\r\n<p align="left">\r\n	<span style="font-size:14px;"><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;5、完成本项目对论文的最低要求。</span></span> \r\n</p>\r\n<p style="text-indent:18pt;">\r\n	<SPAN style="FONT-SIZE: 9pt; FONT-FAMILY: " Tahoma?,sans-serif;?></span>\r\n</p>\r\n<SPAN style="FONT-FAMILY: " Calibri?,sans-serif;?>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span>\r\n		  ', '杨荣山', '2016-03-11 18:38:33', NULL),
(93, 'C14623F35B7DEE28', '镍钴铝三元锂离子电池正极材料的开发', '全校\r', '\r\n		    <p>\r\n	主要是开发高性能的镍钴铝三元正极材料，提高其电化学储能性能。\r\n</p>\r\n<p>\r\n	要求：2～3人组合（专业不限）\r\n</p>\r\n<p>\r\n	联系方式：<a href="mailto:jianqi@swjtu.cn">jianqi@swjtu.cn</a>; 13018219535.\r\n</p>\r\n		  ', '江奇', '2016-03-11 18:27:54', NULL),
(94, 'AB62665D4E2A48FC', '石墨烯改性锰酸锂锂离子电池的研究', '全校\r', '\r\n		    <p>\r\n	主要是利用石墨烯的特殊优秀性能，开展锰酸锂动力电池高温性能的改性研究。\r\n</p>\r\n<p>\r\n	要求：2～3人组合（专业不限）\r\n</p>\r\n<p>\r\n	联系方式：<a href="mailto:jianqi@swjtu.cn">jianqi@swjtu.cn</a>; 13018219535.\r\n</p>\r\n		  ', '江奇', '2016-03-11 18:26:26', NULL),
(95, '059263282A3FFDC7', '石墨烯/碳纳米管纳米复合材料的制备与储能应用', '全校\r', '\r\n		    <p>\r\n	主要开展石墨烯，碳纳米管及其复合材料的制备，及将其制作成超级电容器，研究其储能性能。\r\n</p>\r\n<p>\r\n	要求：2～3人组合（专业不限）\r\n</p>\r\n<p>\r\n	联系方式：<a href="mailto:jianqi@swjtu.cn">jianqi@swjtu.cn</a>; 13018219535.\r\n</p>\r\n		  ', '江奇', '2016-03-11 18:23:47', NULL),
(96, '6CAA3B7ED458DFAC', '高频列车荷载作用下轨道板抗氯离子渗透试验研究', '土木工程学院\r', '\r\n		    <p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 混凝土抗氯离子渗透性能是影响混凝土结构耐久性能的主要因素。无砟轨道结构作为新型混凝土结构长期暴露于大气环境中，且承受列车荷载的高频、随机重复作用，工作环境十分复杂。高频列车荷载作用下，混凝土结构内孔隙及裂纹发生变化，影响轨道板内氯离子渗透性能，从而改变轨道板耐久使用寿命。因此研究高频列车荷载作用对轨道板抗氯离子渗透特性的影响具有重要意义。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"></span>&nbsp;\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">本研究要求如下：</span> \r\n</p>\r\n<p align="left">\r\n	<span style="font-size:14px;"><SPAN style="FONT-FAMILY: " Calibri?,sans-serif;?>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1</span><span>、有较强动手能力，喜欢做实验，对材料科学感兴趣；</span><SPAN style="FONT-SIZE: 9pt; FONT-FAMILY: " Tahoma?,sans-serif;?> </span></span>\r\n</p>\r\n<p align="left">\r\n	<span style="font-size:14px;"><SPAN style="FONT-SIZE: 9pt; FONT-FAMILY: " Tahoma?,sans-serif;?></span><SPAN style="FONT-FAMILY: " Calibri?,sans-serif;?>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2</span><span>、广泛的资料调研，弄清混凝土中氯离子渗透主要机理；</span><SPAN style="FONT-SIZE: 9pt; FONT-FAMILY: " Tahoma?,sans-serif;?> </span>\r\n</p>\r\n<p align="left">\r\n	<SPAN style="FONT-FAMILY: " Calibri?,sans-serif;?>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3</span><span style="font-family:SimSun;">、分析高频列车荷载对轨道板抗氯离子渗透的影响规律；</span> \r\n</p>\r\n<p style="text-indent:18pt;" align="left">\r\n	<SPAN style="FONT-SIZE: 9pt; FONT-FAMILY: " Tahoma?,sans-serif;?></span>\r\n</p>\r\n<p align="left">\r\n	<SPAN style="FONT-FAMILY: " Calibri?,sans-serif;?>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4</span><span>、完成该项目对论文的最低要求。</span><SPAN style="FONT-SIZE: 9pt; FONT-FAMILY: " Tahoma?,sans-serif;?> <span></span></span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n</span> \r\n<p>\r\n	<span style="font-size:14px;"></span>&nbsp;\r\n</p>\r\n		  ', '杨荣山', '2016-03-11 18:20:36', NULL),
(97, 'D6278BFF01F9A072', '基于振动形式的能量收集器的设计与制作', '全校\r', '\r\n		    <p style="margin-left:0cm;">\r\n	<span style="font-size:12px;">项目背景：</span><span style="font-size:10.5pt;font-family:Tahoma, sans-serif;"></span>\r\n</p>\r\n<p style="margin-left:0cm;text-indent:21.0pt;">\r\n	<span style="font-size:12px;">近年来，随着无线传感网络及便携式电子器件技术的进步与发展，以低功耗智能传感器为系统组成的分布式无线传感器网络的应用范围已从军事领域辐射到包括建筑、交通、医疗卫生等工业和民用领域。通常，传感或微电子器件依靠传统的化学电池提供能量。然而，对于许多不能提供电源、不易更换电池或者易燃易爆等危险场合，电池更换问题难以解决。因此，为了获得长期甚至无限生命周期的自主供电系统，从周围环境中收集闲置或者废弃能量进行二次利用，以便给无线传感网络供电，成了当前世界各国的研究热点。</span><span style="font-size:10.5pt;font-family:Tahoma, sans-serif;"></span>\r\n</p>\r\n<p style="margin-left:0cm;">\r\n	<span style="font-size:12px;">任务：</span><span style="font-size:10.5pt;font-family:Tahoma, sans-serif;"></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:12px;">（</span><span style="font-size:12px;font-family:Tahoma, sans-serif;">1</span><span style="font-size:12px;">）能量收集器的结构设计；（</span><span style="font-size:12px;font-family:Tahoma, sans-serif;">2</span><span style="font-size:12px;">）器件的加工制作；（</span><span style="font-size:12px;font-family:Tahoma, sans-serif;">3</span><span style="font-size:12px;">）器件的性能测试分析。</span><span style="font-size:10.5pt;font-family:Tahoma, sans-serif;"></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:12px;">要求：</span><span style="font-size:10.5pt;font-family:Tahoma, sans-serif;"></span>\r\n</p>\r\n<p style="text-indent:19.5pt;background:white;">\r\n	<span style="font-size:12px;">1</span><span style="font-size:12px;">、对该题目非常有兴趣，</span><span style="font-size:10.5pt;font-family:''MS Mincho'';"></span><span style="font-size:12px;">具有较强的动手实践能力。</span><span style="font-size:10.5pt;font-family:Simsun, serif;"></span>\r\n</p>\r\n<p style="text-indent:19.5pt;background:white;">\r\n	<span style="font-size:12px;">2</span><span style="font-size:12px;">、具备良好的团队协作能力。</span><span style="font-size:10.5pt;font-family:Simsun, serif;"></span>\r\n</p>\r\n<p style="text-indent:19.5pt;background:white;">\r\n	<span style="font-size:12px;font-family:Simsun, serif;">&nbsp;</span>\r\n</p>\r\n<p style="text-indent:19.5pt;background:white;">\r\n	<span style="font-size:10.5pt;"></span><span style="font-size:12px;">建议不同专业的同学交叉组队</span><span><span style="font-size:12px;">（共招募</span><span style="font-size:12px;">3-5</span><span style="font-size:12px;">名同学）</span></span><span style="font-size:10.5pt;font-family:''MS Mincho'';"></span><span style="font-size:12px;">！</span><span style="font-size:10.5pt;font-family:Simsun, serif;"></span>\r\n</p>\r\n<p style="text-indent:19.5pt;background:white;">\r\n	<span style="font-size:12px;font-family:Simsun, serif;">&nbsp;</span>\r\n</p>\r\n<p style="background:white;">\r\n	<span style="font-size:12px;font-family:Calibri, sans-serif;">Email</span><span style="font-size:12px;">：</span><span style="font-size:10.5pt;font-family:Calibri, sans-serif;"><span style="font-size:12px;">&nbsp;</span><a href="mailto:weili1812@swjtu.edu.cn"><span style="font-size:12px;">weili1812@swjtu.edu.cn</span></a><span style="font-size:12px;">&nbsp; &nbsp;</span></span><span style="font-size:10.5pt;font-family:Simsun, serif;"></span>\r\n</p>\r\n		  ', '邓维礼', '2016-03-11 17:12:12', NULL),
(98, 'FEF4C389AF2ADB7F', '在校大学生（研究生）校外租房调查', '全校\r', '\r\n		    <p>\r\n	现在大学生多元化，出于不同需要考虑，大学生校外租房现象比较突出。本研究将对校园大学生进行调查，分析大学生校园外租房的规模与比例、校园租房原因（寻求考研环境、情侣恋爱、个人独立自由、校外学习与实习），分析这些学生的家庭背景与经济条件及个人特征，为大学更好了解小康时代、个性化时代、多元化时代大学生的状况与大学内在的服务与管理不足提供有价值的意见。\r\n</p>\r\n<p>\r\n	（1）3-5人，最好不同年级与专业背景组织\r\n</p>\r\n<p>\r\n	（2）个别可以有校园租房经验（不是必要）\r\n</p>\r\n<p>\r\n	（3）具有社会调查能力与沟通能力\r\n</p>\r\n<p>\r\n	（4）研究内容：大学生校园外租房的规模与比例、校园租房原因（寻求考研环境、情侣恋爱、个人独立自由、校外学习与实习），租房学生的家庭背景与经济条件及个人特征、校外租房的利弊、学校的应对之策\r\n</p>\r\n<p>\r\n	（5）调查方式：访谈与问卷，可以对交大周围几所大学学生进行调查，如交大、电子科大、西华大学。\r\n</p>\r\n<p>\r\n	（6）成果：研究报告，相关调查论文\r\n</p>\r\n		  ', '钱志鸿', '2016-03-11 16:12:01', NULL),
(99, '3DBA7860BED843F2', '成都商品楼盘小区公共资源配套调查', '全校\r', '\r\n		    <p>\r\n	城市扩张与房地产的发展，在大都市周边形成了大片新的商品房住宅小区。然而由于公共资源的配套程度不同，导致不同商品房楼盘对消费者的吸引力与居住价值不一。本研究将对金牛区与郫县犀浦镇交大周边的不同地段与成熟程度不同的商品房楼盘小区展开调查。\r\n</p>\r\n<p>\r\n	（1）研究队伍3人即可\r\n</p>\r\n<p>\r\n	（2）具有良好的社会调查兴趣与沟通能力\r\n</p>\r\n<p>\r\n	（3）关注商品房楼盘。\r\n</p>\r\n<p>\r\n	（4）本调查主要是调查商品房楼盘的区位、交通、停车场（车位）、医院、学校、菜市场、购物、社区生活服务等相关的公共资源信息与配套完善程度，对此将依据区域经济学、经济地理学、公共管理等相关知识进行研究分析。将实际配置与业主的反映两方面来展开分析。将选取不同地段、不同成熟程度的商品房住宅小区进行比较研究，反映差异，寻找影响配置的主要因素与楼盘社区的资源配套规律。\r\n</p>\r\n<p>\r\n	（5）研究方式将以集体讨论、集体调查、分工撰写，老师指导与把关，最后提交研究报告。将其中有价值的内容投稿发表。\r\n</p>\r\n		  ', '钱志鸿', '2016-03-11 15:49:50', NULL),
(100, '9F0CC4549B632FD6', '成都郊区城市化农民的生存状况研究', '公共管理学院\r', '\r\n		    <p>\r\n	大都市的扩张与快速城市化推进,，大都市郊区农民迅速被城市化，土地被征用，村民被集中居住。农民住进了安置楼房，分得补偿款，但是就业、社会保障、经济支撑究竟怎样？本研究将选择若干不同类型安置的城市化社区，对此展开社会调查，分析与研究城市化农民的生存现状特点、面临的主要问题、以及对策。\r\n</p>\r\n<p>\r\n	要求：\r\n</p>\r\n<p>\r\n	（1）先通过文献了解我国大都市郊区农民的城市化方式、征地补偿政策、城市化农民享有的社会保障等。\r\n</p>\r\n<p>\r\n	（2）选择典型安置社区展开访谈与调查，考察不同类型的城市化社区的居民生活状况，包括住房、经济来源渠道、就业状况、社会保障、社区服务等。\r\n</p>\r\n<p>\r\n	（3）室内研究，交流、探讨与分析，分工撰写，完成研究报告。将相关有价值的认识以文章形式发表。\r\n</p>\r\n		  ', '钱志鸿', '2016-03-11 15:32:29', NULL),
(101, '1DF7EC28A80A9E0F', '超弹性镍钛形状记忆合金冲击动态力学性能研究', '力学与工程学院\r', '\r\n		    &nbsp;\r\n<p>\r\n	<span style="font-size:9pt;">项目背景：超弹性镍钛形状记忆合金作为一种智能材料，具有很高的能量输出和可恢复变形能力，在应用于减震吸能结构结构中时，经常经历冲击动态载荷作用。目前，对于超弹性形状记忆合金在准静态载荷作用的疲劳性能研究较多，对于动态冲击载荷下该合金的动态力学性能、乃至循环冲击载荷下的动态力学响应研究还是十分缺乏。因此，本项目拟通过霍普金森杆冲击装置对超弹性镍钛形状记忆开展动态冲击实验，揭示材料在单次拉伸</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">/</span><span style="font-size:9pt;">压缩和循环拉伸</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">/</span><span style="font-size:9pt;">压缩情形下的响应，分析已有金属材料冲击动态本构模型的适用性，并试图提出改进的宏观唯象一维本构模型。</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;">任务要求：</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">1</span><span style="font-size:9pt;">）在九里校区开展动态冲击试验；</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">2</span><span style="font-size:9pt;">）处理试验数据，拍摄冲击后的试样形貌加以分析；</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">3</span><span style="font-size:9pt;">）研究该合金与常规金属材料动态冲击性能的差异以及与该材料在准静态加载条件下力学响应的差异，用传统的</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">J-C</span><span style="font-size:9pt;">动态冲击本构模型进行预测，检验其可行性；</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">4</span><span style="font-size:9pt;">）尝试提出改进的一维宏观唯象本构模型。</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;">学习使用设备：</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">MTS</span><span style="font-size:9pt;">（静态试验）、霍普金斯杆（冲击试验）</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;">学习软件：</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"> Origin</span><span style="font-size:9pt;">，</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">Excel</span><span style="font-size:9pt;">，</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">Word</span><span style="font-size:9pt;">和</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">Powerpoint</span><span style="font-size:9pt;">。</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;">人数：</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">4</span><span style="font-size:9pt;">人，可在后续的毕业设计中延续该项研究。</span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"></span>\r\n</p>\r\n		  ', '康国政', '2016-03-11 09:48:38', NULL),
(102, 'CF526CAB9E64F4E8', '高速铁路轮轨滚动接触棘轮行为有限元分析', '力学与工程学院\r', '\r\n		    &nbsp;\r\n<p>\r\n	<span><span style="font-size:9pt;">项目背景：世界的高铁看中国，中国高铁正面临着飞跃性的发展，在高速铁路里程激增的同时，未来也会因此带来沉重的维护成本。高速铁路运行速度在</span></span></a><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">250Km/h</span></span></span><span><span><span style="font-size:9pt;">以上，滚动接触的频率非常高，由局部塑性变形循环累积产生的棘轮行为将导致钢轨的疲劳失效。通过材料试验，获取车轮和钢轨材料的力学特性和循环特性，建立循环塑性本构模型和轮轨滚动接触有限元分析模型，研究在高速运行状态下，钢轨和车轮不同选材匹配下的接触应力和塑性变形累积规律，研究结果将为高速铁路轮轨选材提供有益参考，进而对钢轨和车轮进行延寿。</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span style="font-size:9pt;">任务要求：</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">1</span></span></span><span><span><span style="font-size:9pt;">）熟悉轮轨接触的背景知识和循环塑性的基础知识；</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">2</span></span></span><span><span><span style="font-size:9pt;">）获取钢轨和车轮材料参数；</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">3</span></span></span><span><span><span style="font-size:9pt;">）建立轮轨接触有限元模型；</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">4</span></span></span><span><span><span style="font-size:9pt;">）计算不同工况下轮轨棘轮行为演化特征。</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span style="font-size:9pt;">学习软件：</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">ABAQUS</span></span></span><span><span><span style="font-size:9pt;">，</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"> <span>ANSYS</span></span></span></span><span><span><span style="font-size:9pt;">，</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">Origin</span></span></span><span><span><span style="font-size:9pt;">，</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">Excel</span></span></span><span><span><span style="font-size:9pt;">，</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">Word</span></span></span><span><span><span style="font-size:9pt;">和</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">Powerpoint</span></span></span><span><span><span style="font-size:9pt;">。</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span style="font-size:9pt;">人数：</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;">4</span></span></span><span><span><span style="font-size:9pt;">人，可在后续的毕业设计中延续该项研究。</span></span></span><span><span><span style="font-size:9pt;font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;;"></span></span></span>\r\n</p>\r\n		  ', '康国政', '2016-03-11 09:47:46', NULL),
(103, '9CF3F49BA94D3ABD', '大学生自我管理能力现状调查与研究', '全校\r', '\r\n		    <p>\r\n	&nbsp;<span style="font-size:24pt;font-family:宋体;font-weight:bold;color:#1f497d;"><span style="font-size:16px;">自我管</span><span style="font-size:12px;"></span><span style="font-size:16px;">理</span></span><span style="font-size:14px;font-family:宋体;color:black;"><span style="font-size:16px;">主要指个人充分调动自身的主观能动性，有效利用和整合自我资源（时间、心理、身体、行为和信息等），充分开展自我认知、自我计划、自我组织、自我控制、自我监督等一系列自我学习、自我教育、自我发展的活动。我们</span><span style="font-size:24pt;font-family:宋体;color:black;"><span style="font-size:16px;">依靠自己的</span><span style="font-size:16px;">主观能动性按照社会目标，有意识、有目的地对自己的思想、行为进行转化控制的能力就称为自我管理能力</span><span style="font-size:16px;">。当前，大学生的自我管理能力普遍缺乏。本项课题就是通过访谈、问卷调查等方法对我校学生的自我管理能力现状开展调查，并开展研究分析，</span><span style="font-size:16px;">形成调查报告。要求参与学生具有沟通能力，有一定的问卷设计及分析能力。欢迎有兴趣的同学加入,联系方式：qq，466077729；邮箱：<a href="mailto:lijinhong@swjtu.edu.cn">lijinhong@swjtu.edu.cn</a></span></span></span>\r\n</p>\r\n		  ', '李锦红', '2016-03-11 08:52:59', NULL),
(104, 'F7709BCBF668A952', '波形钢腹板预应力混凝土箱梁剪力滞效应分析', '土木工程学院\r', '\r\n		    利用有限元软件，对波形钢腹板预应力混凝土箱梁剪力滞效应进行分析，计算弯矩作用下桥面板应力分布。培养学生一定的结构分析能力，理解有限元数值模拟方法，了解钢-混凝土组合结构桥梁典型问题主要分析方法。\r\n		  ', '卫星', '2016-03-10 23:31:16', NULL),
(105, '1F1EB3F7688AA5BA', '钢桥典型焊接细节缺口应力分析', '全校\r', '\r\n		    利用有限元软件，对钢桥典型焊接细节进行三维数值模拟，确定焊接细节焊根处缺口应力。培养学生一定的结构分析能力，理解有限元数值模拟方法，了解钢结构桥梁疲劳问题主要分析方法。\r\n		  ', '卫星', '2016-03-10 23:01:32', NULL),
(106, '9073D8557DE1F684', '钢桥典型焊接细节热点应力分析', '全校\r', '\r\n		    利用有限元软件，对钢桥典型焊接细节进行三维数值模拟，确定焊接细节焊根处热点应力。培养学生一定的结构分析能力，理解有限元数值模拟方法，了解钢结构桥梁疲劳问题主要分析方法。\r\n		  ', '卫星', '2016-03-10 22:57:29', NULL),
(107, '8C09089C373E8492', '城市少数民族聚居区空间研究', '建筑学院\r', '\r\n		    城市少数民族聚居区的空间研究以个案为基础，对空间形态做出归因分析，空间句法分析等等 。\r\n		  ', '田凯', '2016-03-10 22:19:11', NULL),
(108, 'ED45C6A07709CA1D', '基于ArcPy的规则格网DEM质量检查与评价原型系统', '全校\r', '\r\n		    <p>\r\n	基于ArcPy的规则格网DEM质量检查与评价原型系统\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	1、DEM质检与评价方法研究：包括DEM高程精度评定、接边精度检查、等高线套合差检查、格网点粗差检查等方法\r\n</p>\r\n<p>\r\n	2、规则格网DEM质量检查与评价原型系统功能设计\r\n</p>\r\n<p>\r\n	3、基于Python开发环境实现DEM质检功能\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	联系方式：<a href="mailto:7476815@qq.com">7476815@qq.com</a>\r\n</p>\r\n		  ', '黄泽纯', '2016-03-10 22:12:53', NULL),
(109, '2016A4F9BCD5AC66', '利用主成分分析简化LiDAR点云', '全校\r', '\r\n		    <p>\r\n	利用主成分分析简化LiDAR点云\r\n</p>\r\n<p>\r\n	1、LiDAR点云简化方案设计\r\n</p>\r\n<p>\r\n	2、LiDAR点云简化效果评价方法\r\n</p>\r\n<p>\r\n	3、实验与分析\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	联系方式：<a href="mailto:7476815@qq.com">7476815@qq.com</a>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n		  ', '黄泽纯', '2016-03-10 21:50:20', NULL),
(110, 'A256AADB816EF491', 'C市某片区居民公交出行便利性研究', '全校\r', '\r\n		    <p>\r\n	<span style="font-family:宋体;white-space:normal;background-color:#F7F7F7;">基于C市公交骨干网络的拓扑结构，对某片区居民公交出行的便利性进行研究。</span>\r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;white-space:normal;background-color:#F7F7F7;"><span style="font-family:宋体;white-space:normal;background-color:#F7F7F7;">学生需要进行广泛的文献调研，并需要掌握一定的数据处理和分析的知识与技能。</span><br />\r\n</span>\r\n</p>\r\n		  ', '耿维', '2016-03-10 21:48:41', NULL),
(111, '70EFECB4AD0F3904', '基于Web的实验设备预约管理系统设计与开发', '全校\r', '\r\n		    <p>\r\n	基于PHP的实验设备预约管理系统设计与开发\r\n</p>\r\n<p>\r\n	1、实验设备预约流程设计\r\n</p>\r\n<p>\r\n	2、Web系统框架与功能设计\r\n</p>\r\n<p>\r\n	3、编码与功能实现\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	涉及到的技术：PHP、HTML、CSS、Javascript、MySQL、Apache\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	联系方式：<a href="mailto:7476815@qq.com">7476815@qq.com</a>\r\n</p>\r\n		  ', '黄泽纯', '2016-03-10 21:39:38', NULL),
(112, 'EF4ECDAA553D6720', '教师教学满意度调查研究（以西南交通大学为例）', '全校\r', '\r\n		    <p>\r\n	目标和任务\r\n</p>\r\n<p>\r\n	1）了解本科教学情况及教学中存在的问题；\r\n</p>\r\n<p>\r\n	2）设计问卷进行调查、统计和分析；\r\n</p>\r\n<p>\r\n	3）撰写研究报告；\r\n</p>\r\n<p>\r\n	4）和我共同完成一篇高质量的教学改革方面学术论文。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	要求：有一定的经济学、管理学和统计分析能力，能够独立完成调查和统计分析。\r\n</p>\r\n<p>\r\n	人数：3-5人\r\n</p>\r\n<p>\r\n	联系方式：<a href="mailto:douxiangsheng@tsinghua.org.cn">douxiangsheng@tsinghua.org.cn</a> \r\n</p>\r\n		  ', '窦祥胜', '2016-03-10 20:43:09', NULL);
INSERT INTO `srtphire` (`id`, `oid`, `title`, `orienting`, `content`, `author`, `original_time`, `breviary`) VALUES
(113, '0E4C68D2171BAEEA', '大学生学习勤奋度调查研究（以西南交通大学为例）', '全校\r', '\r\n		    <p>\r\n	目标和任务\r\n</p>\r\n<p>\r\n	1）了解本科大学生的学习状况及存在的问题；\r\n</p>\r\n<p>\r\n	2）设计问卷进行调查、统计和分析；\r\n</p>\r\n<p>\r\n	3）撰写研究报告；\r\n</p>\r\n<p>\r\n	4）和我合作撰写一篇高质量的教学方面的学术论文。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	要求：要求有一定的经济学、管理学和统计分析能力，能够独立完成调查和统计分析。\r\n</p>\r\n<p>\r\n	人数：3-5人\r\n</p>\r\n<p>\r\n	联系方式：<a href="mailto:douxiangsheng@tsinghua.org.cn">douxiangsheng@tsinghua.org.cn</a>\r\n</p>\r\n		  ', '窦祥胜', '2016-03-10 20:40:30', NULL),
(114, '603BADCE36B71828', '对2016年美国总统大选的英文报道的修辞研究', '外国语学院\r', '\r\n		    <p>\r\n	<span>美国大选在</span><span><span>2016</span></span><span>年是一个热点话题，主流英文媒体对此有大量和深入的</span> \r\n</p>\r\n<p>\r\n	<span>报道。这对英语学习者和英语翻译者来说是一个难得的学习机会。通过语言的学习</span> \r\n</p>\r\n<p>\r\n	<span>深入了解美国文化的一部分即美国大选文化。</span> \r\n</p>\r\n<p>\r\n	<span>此次任务为：</span> \r\n</p>\r\n<p style="text-indent:-18pt;">\r\n	<span><span><span>1．</span><span> </span></span></span><span>对美国总统选举制度前期学习</span> \r\n</p>\r\n<p style="text-indent:-18pt;">\r\n	<span><span><span>2．</span><span> </span></span></span><span>梳理相关的新闻报道</span> \r\n</p>\r\n<p style="text-indent:-18pt;">\r\n	<span><span><span>3．</span><span> </span></span></span><span>研究报道中的语言现象</span> \r\n</p>\r\n<p style="text-indent:-18pt;">\r\n	<span><span><span>4．</span><span> </span></span></span><span>尝试性总结美国大选中的修辞和文化的关系。</span>\r\n</p>\r\n<p style="text-indent:-18pt;">\r\n	<span>联系方式：6281919@qq.com</span>\r\n</p>\r\n<p style="text-indent:-18pt;">\r\n	<br />\r\n</p>\r\n		  ', '李勇', '2016-03-10 16:11:30', NULL),
(115, '4CFBDCE1B1B34795', '客货混运线路钢轨选型有限元分析', '力学与工程学院\r', '\r\n		    &nbsp;<span><span><span style="font-size:9pt;">项目背景：在客货混运的线路，列车开行速度和频次十分复杂，导致该线路钢轨磨耗严重，其钢轨维护成本明显高于客运线路。通过有限元软件，建立轮轨接触动力学模型，计算在不同轴重和运行速度下的接触应力和接触斑形状。通过调整钢轨顶面尺寸设计，研究最佳的优化尺寸，降低轮轨接触应力，从而为钢轨延寿提纲依据。</span></span></span></a><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';"></span></span></span></span> \r\n<p>\r\n	<span><span><span><span style="font-size:9pt;">任务要求：</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';">1</span></span></span></span><span><span><span><span style="font-size:9pt;">）熟悉轮轨接触的背景知识；</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';">2</span></span></span></span><span><span><span><span style="font-size:9pt;">）获取钢轨和车轮材料参数；</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';">3</span></span></span></span><span><span><span><span style="font-size:9pt;">）建立轮轨接触有限元模型；</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';">4</span></span></span></span><span><span><span><span style="font-size:9pt;">）计算不同工况下接触应力和接触斑大小；</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';"></span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span style="font-size:9pt;">将要学习的软件：</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';">ABAQUS</span></span></span></span><span><span><span><span style="font-size:9pt;">，</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';">Origin</span></span></span></span><span><span><span><span style="font-size:9pt;">，</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';">ANSYS</span></span></span></span><span><span><span><span style="font-size:9pt;">，</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';">Excel</span></span></span></span><span><span><span><span style="font-size:9pt;">，</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';">Word</span></span></span></span><span><span><span><span style="font-size:9pt;">和</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';">Powerpoint</span></span></span></span><span><span><span><span style="font-size:9pt;">。</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';"></span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span style="font-size:9pt;">人数：</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';">3</span></span></span></span><span><span><span><span style="font-size:9pt;">人，大三的优先考虑，后续的毕业设计也可以持续</span></span></span></span><span><span><span><span style="font-size:9pt;font-family:''Tahoma'',''sans-serif'';"></span></span></span></span>\r\n</p>\r\n		  ', '阚前华', '2016-03-10 16:09:47', NULL),
(116, '33DADA141D39F7AE', '基于同相供电系统的监控调试系统设计与实现', '电气工程学院\r', '\r\n		    <p>\r\n	<span style="white-space:nowrap;">&nbsp; &nbsp; &nbsp;&nbsp;现有铁路牵引供电系统采用三相交流电分相进行供电。同相供电系统实现牵引网电压同相位运行，有效解决了长</span>\r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;">期困扰铁路过分</span><span style="line-height:1.5;white-space:nowrap;">相和</span><span style="line-height:1.5;white-space:nowrap;">电能质量的问题。由西南交大主持承担的国家重大科研项目——电气化铁路同相供电装置已</span><span style="line-height:1.5;white-space:nowrap;">投入</span>\r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">试运行。监控<span style="white-space:nowrap;">调试</span>系统在同相供电系统安全、可靠的运行中起到至关重要的关键作用。现运行的</span><span style="line-height:1.5;white-space:nowrap;">同相供电系统亟需性</span>\r\n</p>\r\n<p>\r\n	<span style="line-height:1.5;white-space:nowrap;">能良好的监控系统支持</span><span style="line-height:1.5;white-space:nowrap;">，要求运行可靠、使用简便。监控调试系统采用LabVIEW设计。</span>\r\n</p>\r\n<span style="white-space:nowrap;"><br />\r\n</span><br />\r\n<span style="white-space:nowrap;"><br />\r\n</span><br />\r\n<span style="white-space:nowrap;">要求：希望对程序设计和LabVIEW编程有一定基础和兴趣的电气工程学院大二、大三同学报名。</span><br />\r\n<span style="white-space:nowrap;"><br />\r\n</span><br />\r\n<span style="white-space:nowrap;">联系电话：15608027626&nbsp;</span><br />\r\n		  ', '张湘', '2016-03-10 14:51:47', NULL),
(117, '3518C32B190A9A3C', '基于单片机的调压开关电源设计与实现', '电气工程学院\r', '\r\n		    <p>\r\n	<span style="white-space:nowrap;"> 电源技术是应用电力电子半导体器件，综合自动控制、计算机(微处理器)技术和电磁技术的多学科边缘交叉技术。</span> \r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;">在各种高质量、高效、高可靠性的电源中起关键作用，是现代电力电子技术的具体应用。调压开关电源在传统电源的</span> \r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;">基础上增加了以单片机为基础的数字控制部分，通过软件模拟电位器来改变开关电源反馈电压，从而改变输出电压的</span> \r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;">大小。这样，电源就可以为需要不同电压的电器设备供电。数控调压电源克服了普通电源只能输出恒定电压的</span><span style="white-space:nowrap;line-height:1.5;">缺点，</span> \r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;line-height:1.5;">它能在其他硬件设备的支持下通过单片机编程来改变电压的输出范围和步近系数，具备操作自动化、上手容</span><span style="white-space:nowrap;line-height:1.5;">易、显</span><span style="line-height:1.5;white-space:nowrap;">示</span> \r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;line-height:1.5;">直观等优点，能够为智能化数字仪表设备提供稳定可靠的输出电压。</span> \r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style="white-space:nowrap;"><br />\r\n</span> \r\n</p>\r\n<p style="text-align:left;">\r\n	<span style="white-space:nowrap;"> 要求：希望有电工技术、电子技术和单片机基础的电气工程学院的大二、大三同学报名。</span> \r\n</p>\r\n<p style="text-align:right;">\r\n	<span style="white-space:nowrap;line-height:1.5;"><br />\r\n</span> \r\n</p>\r\n<p style="text-align:left;">\r\n	<span style="white-space:nowrap;line-height:1.5;"></span><span style="white-space:nowrap;line-height:1.5;"> 联系电话：15608027626 </span> \r\n</p>\r\n<p style="text-align:left;">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n		  ', '张湘', '2016-03-10 14:34:15', NULL),
(118, '5468B14357585DA1', '世界大跨混凝土桥垮塌事故分析', '土木工程学院\r', '\r\n		    <span style="white-space:nowrap;">对世界大跨混凝土桥垮塌事故，采用大型有限元软件建模分析，利用所学力学知识分析垮塌过程和原因，重现垮塌现场。</span>\r\n		  ', '叶华文', '2016-03-10 11:55:48', NULL),
(119, '476AF83B6C54B1B9', '基于网络大数据的旅游适宜区动态制图技术', '地球科学与环境工程学院\r', '\r\n		    <p>\r\n	1、需要学生熟练掌握GIS的理论与技术；\r\n</p>\r\n<p>\r\n	2、需要学生有良好的数理基础；\r\n</p>\r\n<p>\r\n	3、熟练的计算机编程技术；\r\n</p>\r\n<p>\r\n	4、对科学研究浓郁的兴趣和锲而不舍的钻研精神\r\n</p>\r\n<p>\r\n	5、联系方式：<a href="mailto:523091987@qq.com">523091987@qq.com</a> \r\n</p>\r\n		  ', '汤家法', '2016-03-10 11:50:20', NULL),
(120, '4CE26D8C619F6BA4', '新型铁合金的烧结成型机理与性能研究', '全校\r', '\r\n		    <p>\r\n	采用粉末冶金的方法制备铁基新型合金，研究其烧结工艺参数对烧结后性能和微观结构的影响。\r\n</p>\r\n<p>\r\n	要求无重修课，平均分75分以上的大二年级的本科生参加，具有良好的科研素养与兴趣，良好的英文写作基础。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	感兴趣的同学请联系：gdcui@swjtu.edu.cn\r\n</p>\r\n		  ', '崔国栋', '2015-10-27 14:19:46', NULL);
